@-moz-document domain("narutoforums.com") { 


div#div-gpt-ad-1378318375707-0
{
height: 0px important;
}

.content-bdr {
border: 0px !important;
}

.bdrtop {
border: 0px !important;
}

/** Font Section

*/


.smallfont, .pagenav, .thead, .fieldset, legend, table, tbody, td, input, select, button, p, li, div, span {
    font-size: inherit !important;
 }

body, html, table, tbody, div, span, td, th, label, input, select, button, p, li, dt, dd, .alt1, .alt1Active, .alt2, .alt2Active, .alt1 a, .alt1Active a, .alt2 a, .alt2Active a {
    font-family: "calibri" !important;
    
}

 /** Mainnav bar & Top Area
 
 */
 
 
 .mainnav {
 background: transparent !important;
 Height: 20px !important;
 Font-size: 12px !important;
 padding: 0px !important;
 }
 
 .vbmenu_control {
 background: transparent !important;
 }
 
 .page-content {
 background: url("http://i.imgur.com/IAwfzvB.png") !important;
 color: #5C5C5C !important;
 border: 0px !important;
 }
 
 .search-container input[type="text"] {
 background: url("http://i.imgur.com/3086cvi.png") !important;
 border: 0px !important;
 color: #F2F0EB !important;
 }
 
 .login-box {
 background: transparent !important;
 color: #F2F0EB !important;
 border: 0px !important;
 }
 
 .login-box p {
 color: #5C5C5C !important;
 }
 
 .login-box td {
 color: #F2F0EB !important;
 border: 0px !important;
 }
 
 .login-box input[type="text"], .login-box input[type="password"] {
 Background: url("http://i.imgur.com/3086cvi.png") !important;
 color: #F2F0EB !important;
 border: 0px !important;
 }
 
 .logo {
 display: None !important;
 }
 
 /** Body and Post Area
 */

body {
    background: url("http://i.imgur.com/qg9qOEb.png") !important;
 
 
 }
 
 .accordianbtn {
 
display: none !important;
}
 
 .tcat {
 background: url("http://i.imgur.com/3086cvi.png") !important;
 height: 20px !important;
 border-radius: 6px;
 }
 
 .tcat a img {
 display: none !important;
 }
 
 .tcat a:link, .tcat_alink
{
	color: #F2E9E1 !important;
	text-decoration: none !important;
}
.tcat a:visited, .tcat_avisited
{
	color: #F2E9E1 !important;
	text-decoration: none !important;
}

.tcat a:hover, .tcat a:active, .tcat_ahover
{
	color: #F2E9E1 !important;
	text-decoration: underline !important;
}

.tcat2 {
background: url("http://i.imgur.com/3086cvi.png") !important;
 height: 20px !important;
 border-radius: 6px;
}
 
.tborder {
 background: #ffffff url("http://i.imgur.com/kglnEkk.png") !important;
 padding: 0px !important;
 }
 
 .thead {
 background: url("http://imgur.com/96Fijmn.png") !important;
 border-radius: 6px;
 color: #f0f0f0 !important;
 }
 
