}

.thead a:visited, .thead_avisited
{
	color: #FFFFFF !important;
}

.thead a:hover, .thead a:active, .thead_ahover
{
	color: #FFFFFF !important;
}

.tfoot {
background: url("http://imgur.com/96Fijmn.png") !important;
 border-radius: 6px;
 color: #f0f0f0 !important;
}
 
 a:link, body_alink
{
	color: #2A363B !important;
	text-decoration: none !important;
}
a:visited, body_avisited
{
	color: #2A363B !important;
	text-decoration: none !important;
}
a:hover, a:active, body_ahover
{
	color: #E94E77 !important;
	text-decoration: underline !important;
}
 
.alt1, .alt1Active
{
	background: #ffffff url("http://i.imgur.com/kglnEkk.png") !important;
	color: #5C5C5C !important;
	border: 0px !important;
	border-radius: 6px;
	
}
.alt2, .alt2Active
{
	background: #ffffff url("http://i.imgur.com/kglnEkk.png") !important;
	color: #5C5C5C !important;
	border: 0px !important;
	border-radius: 6px;
}

.inlinemod
{
	background: #D88A8A !important;
	color: #474843 !important;
}


.time
{
	color: #666686 !important;
}

.navlink-normal, .navlink-hover {
background: #ffffff url("http://i.imgur.com/kglnEkk.png") !important;
}

.navtitle {
background: url("http://imgur.com/96Fijmn.png") !important;
color: #F2E9E1 !important;
border-radius: 6px;
}

.vbmenu_option {
background: #ffffff url("http://i.imgur.com/kglnEkk.png") !important;
border: 0px !important;
}

.vbmenu_popup {
background: #ffffff url("http://i.imgur.com/kglnEkk.png") !important;
padding: 2px !important;
border-radius: 6px;
}

.panel {
background: #ffffff url("http://i.imgur.com/kglnEkk.png") !important;
}

.panelsurround {
background: #ffffff url("http://i.imgur.com/kglnEkk.png") !important;
}

/** Resize Section - The important stuff

 */
 

.page-content {

    

    max-width: 100%;

    min-height: 20%;

    max-height: 80%;

    resize: both;

    

    margin-left: auto;

    margin-right: auto;

}


/** Navigation Bar - Those links like "User CP" and "Log Out". 

 */



.subnav {

    max-width: 40%;

    font: bold 7pt verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif !important;



}



/** Hide the Naruto logo that's floating a bit off to the left

 */

.logo h1 span {

    display: none



}



/** Ad Removal Section - Cannot block links added in text as they're javascript. Use adblock to eliminate those.

 */



.googleadd {

    display: none;



}



.banner-container {

    display: none;



}



.banner1 {

    display: none;



}



.banner2 {

    display: none;

    

}



.content-bdr {

    display: none;



}



.toplinkimg {

    display: none;



}

/**Borders
*/

.tborder, .thead, .tcat, .tfoot, .panel { border: 0 !important; border-spacing: 0 !important; }
.thead, .vBulletin_editor, element, .page-content, fieldset, .login-box input[type="text"], .search-container input[type="text"], .login-box input[type="password"] { border: 0 !important; }
[style*="padding:1px"] { padding: 0px !important; }

}
