css:
@-moz-document url-prefix(http://),
url-prefix(https://),
url-prefix(about:),
url-prefix(file:///),
url("chrome://browser/content/bookmarks/bookmarksPanel.xul"),
url("chrome://browser/content/browser.xul"),
url("chrome://browser/content/history/history-panel.xul"){

/*--------------网页------------------*/

/*样式站广告*/
@-moz-document url-prefix(https://userstyles.org/){
.overlay_background{display:none}}

/*B站*/
@-moz-document url-prefix(https://member.bilibili.com/v/video/),url-prefix("http://member.bilibili.com/v2#/upload-manager"),url-prefix("https://member.bilibili.com/"){

.bili-modal .modal {margin-left:450px!important;box-shadow:rgba(0,0,0,0.3) 0px 0px 5px}
.bili-modal .overlay {display:none}/*上传页换封面图时的暗色限制*/   

/*B站-视频管理：弹幕转移 - 弹出面板标题显示宽度调整*/
.paginate-name {width:270px!important;}/*标题*/
.paginate-name I{color:red!important;}/*标题前：[已删除]*/
.dropdown-menu{width:650px!important;margin-left:-100px;}/*外框*/
.option-menu{margin-top:-10px;}/*弹幕管理页下拉菜单上移，解决鼠标总是点不到菜单的问题*/

/*投稿页-弹幕举报整体宽度*/
.cc-container.danmu-report.cc-content-body{width:90%!important;margin-left:10px}
.danmu-video{width:30%!important;}/*投稿页-弹幕举报-弹幕视频标题宽度*/

}

/*“声音搜索”高级搜索框位置*/
#voiceSearchAdvanced {margin-top:100px !Important}

/*搜狐视频VIP遮挡广告*/
#player_vipTips {display:none !important;}

/*爱奇艺*/
@-moz-document url-prefix(http://www.iqiyi.com/){
[class*="J_play-underFrame"] {display:none!important;}
DIV[style*="height: 417px;"] {margin-top:400px;}
.playPay-popup-body,DIV[style*="width: 1060px"] {display:none}}

/*一直播*/
@-moz-document url-prefix(http://www.yizhibo.com/){
#J_resize,.gift_his.J_scroll,#J_giftaction,#J_reg_guide{display:none !important;}}

/*知乎*/

/*豆瓣*/
@-moz-document url-prefix(https://www.douban.com){

/*悬浮 - 大屏16：9*/
body{margin-top:25px}
#db-global-nav{position: fixed;z-index:13;width:100%!important;margin-top:-25px;} /*豆邮、帐号*/
.top-nav-info{margin-right:400px!important;}

.group-topic-search {position:fixed;top:170px !important;margin-left:650px !important;} /*小组搜索置顶*/
.group-topic-search {background:rgba(288,288,288,0.5);}/*小组搜索框颜色*/
.group-topic-search:hover,.group-topic-search:focus {background:rgba(288,288,288,0.8) !important;}/*小组搜索框背景*/

.lnk-doubanapp {display:none  !important;} /*右上APP广告*/

}

/*鼠标移到图片上发光- by:网络孤独行客*/
img:hover{transition-duration: 0.32s;-webkit-transition-duration: 
0.32s;transition-property:box-shadow !important;box-shadow: 0px 0px 4px 
4px rgba(100,220,250,1) !important;}

/*===========================火狐自身===========================*/

/*全局监控流量-地址栏扩展*/
#SpeedMonitorPanel *{color:/*[[color-7]]*/ !important;text-shadow: /*[[color-8]]*/ 0px 0px 1px !important;font-size:15px!important;}

/*全局流量监控文字-地址栏扩展*/
#SpeedMonitorPanel *{color:#fff !important;text-shadow: #000 0px 0px 2px !important;font-size:15px!important;}

/*全局流量监控模块+仿旧版火狐界面扩展自带的刷新及停止按钮*/
#SpeedMonitorPanel,#urlbar-reload-button,#urlbar-stop-button{margin-top:-1px;border:transparent !important;margin-left:-1px;margin-right:-1px;}

/*屏蔽附加组件栏上的关闭按钮“X”*/
#addonbar-closebutton {display: none !important;}

/*扩展管理器显示地址栏-*/
#main-window[disablechrome] #navigator-toolbox[tabsontop="true"] #nav-bar:not([collapsed="true"])
{visibility:visible !important;}

/*火狐浏览器地址栏上的图标*/
#identity-icon-labels /*火狐安全页面文字标识“firefox”*/ ,
#notification-popup-box /*adobe Flash 是否允许启用*/ {display: none !important;} 

/*查找栏计数器颜色*/
.findbar-find-status {color:#FF0000 !important;}

/*防止样式编辑器下方按钮被过多的报错提示（错误）挤掉*/
@-moz-document url-prefix("chrome://stylish/content/edit.xul") { #errors {outline:solid 1px #aaa!important;height:300px !important;}} 

/*各按钮的下三角箭头*/ /*[[style-1]]*/

/*进度条样式*/
.progress-bar {
-moz-appearance: none !important;
background-color: #fff !important;
background-size: 10px 10px;
background-image: linear-gradient(45deg, #000 25%, transparent 26%,
transparent 50%, #000 51%, #000 75%,
transparent 76%, transparent);
-moz-animation: animate-stripes 3s linear infinite;
}
 
@-moz-keyframes animate-stripes {
0% {background-position: 0 0;}
 
100% {background-position: 60px 0;}}







/*===========Firefox橙色菜单栏变透明,去文字换图标,调宽度,去小三角============*/

#appmenu-button{ border-radius:0px !important;} /*火狐橙形状*/

#appmenu-button{ background: transparent !important;
list-style-image:url( 

http://1.jpg

);/*换图位*/

background:#F6F6F6 !important;
background:none !important;
min-width:30px ;
margin-left:-4px !important;/*位置与宽度*/
margin-top:-3px !important;/*上下位置*/
margin-bottom:-1px !important;/*新增*/

padding-top:5px !important; 
padding-left:2px !important; 
padding-right:3px !important;

box-shadow:none !important;
border:none !important; }

#appmenu-button .button-text {display: none !important;} 
#appmenu-button dropmarker {display: none !important;}

/*===========Firefox橙色菜单自定义文字（浮舟RY 提供）============*/

/*删原有文字*/
#appmenu-button .button-box .box-inherit .button-text { display: none !important;}

/*替换火狐橙文字*/
#appmenu-button .button-box .box-inherit:before {
content: "　HCY48　"; /*自定义文字*/

/*字体属性*/
color:#AAAAAA !important; /*文字颜色*/
font-size:12px; /*文字大小*/
font-weight: bold; /*文字粗细属性*/
margin-left:4px !important; /*文字位置*/
text-shadow:1px 1px 5px rgba(0,0,0,0) ; /*文字阴影*/
font-family:"tahoma","微软雅黑" ;/*火狐橙字体：前英后中*/

}

/*====================火狐右上角三按钮（最大化、最小化、关闭）====================*/

#main-window:not([inFullscreen]) #titlebar-min, #main-window:not([inFullscreen]) #titlebar-max, #main-window:not([inFullscreen]) #titlebar-close {
list-style-image: none !important;
}
