background-image: url("http://imgsrc.baidu.com/forum/pic/item/8ae09acad1c8a786d81494e76609c93d71cf5018.jpg") !important;
}
#main-window[sizemode="maximized"]:not([inFullscreen]) #titlebar-max:active { /*点击瞬间-窗口化*/
background-image: url("http://imgsrc.baidu.com/forum/pic/item/fb1839d12f2eb9384081a51bd4628535e7dd6f6f.jpg") !important;}

/*====================浏览器窗口透明化（WIN7及以上系统需开启AERO效果）====================*/

/*导航栏*/
#FindToolbar,#browser-bottombox,#nav-bar{background:rgba(288,288,288,0)!important;}

/*查找栏+地址栏+工具栏*/
toolbar,#nav-bar textbox{background:rgba(288,288,288,0.3)!important;border:none !Important;transition-duration: 0.5s;}
#FindToolbar{background:rgba(288,288,288,0.3)!important;}
#nav-bar textbox:hover{background:rgba(288,288,288,0.8)!important;transition-duration: 0.5s;}


}

