/*百度首页天气-城市名*/
.show-city {opacity:0 !important;margin-left:-10px !Important;transition-duration: 0.5s;} 
.show-city:hover {opacity:1 !important;margin-left:10px !Important;transition-duration: 0.5s;}
/*京东、淘宝用户名、地址*/
#store-selector,#jdarea,.summary .text,.login-info-nick,#J_AddrSelectTrigger{opacity:0 !important;transition-duration: 0.5s;} 
#store-selector:hover,#jdarea:hover,.summary .text:hover,.login-info-nick:hover,#J_AddrSelectTrigger:hover {opacity:1 !important;transition-duration: 0.5s;}

