css:
@-moz-document url-prefix("http://www.iltalehti.fi/") {

/* Custom styles for Iltalehti website */

/* - Hide unneeded page elements. */
#mainos,
#jatti,
#oikeamainos,
#hattunavi,
#navinosto1,
#navinosto2,
#navihaku,
.alavarjo,
.ylavarjo,
.palstakuva,
.nauhakuva,
.plus-promo-kainalo,
.plus-promo-bottom,
#kp_share_container,
#kp_share_keskustelufoorumi,
.kp_luetuimmat_nyt,
.fb-kommentointi-otsikko,
.fb-comments,
.fb-comments-lightscheme,
.fb_iframe_widget,
.leikiwidget,
.EAS,
#kl_nosto_13,
.pvn_kolme_luetuinta_nosto,
.pvn_kolme_luetuinta_digi,
.widget--plusnosto,
.widget--iltv,
.widget--viihde,
.widget--uutiset,
.widget--urheilu,
.widget--fiidifi,
.widget--terveys,
.widget--tyylicom,
.widget--asuminen,
.widget--autot,
.widget--matkailu,
.widget--ruoka,
.widget--kotikokki-suosittelee,
.widget--rakkausjaseksi,
.widget--ekontaktiad,
.widget--nainen,
.widget--perhe,
.widget--mielipide,
.widget--blogit,
.widget--saa,
.widget--paivan-lehti,
.widget--mobiili,
.widget--social,
.widget--fingerpori,
.widget--horoskooppi,
.widget--lukijankuva,
.widget--hullumaailma,
.widget--julkkislista,
.widget--leffat,
.widget--15kysymysta,
.widget--100kysymysta,
.widget--iltvkatsotuimmat,
.widget--iltv-katsotuimmat,
.widget--pelit,
.widget--pelit,
.widget--figma,
.widget--digipoll,
.widget--pelaajalehti,
.widget--edukas,
.widget--iltab,
.iltab_yla_container,
.widget--telkku,
.palstaviiva,
#programTablePage,
#container_footer,
#monsterWrapper,
.vinjetti,
.nollamargin,
.kehyselem,
#mp_monster,
.paivantalous,
.site-search,
#telkku-crnosto-wrapper {
  display: none !important;
}

#content,
#alanavi {
  background: transparent;
}




/* - Print styles ------------------------------------------------- */
@media print {

  /* - Print styles: Increase text size and line height to make article more readable. */
  body {
    font-size: 16px;
    line-height: 24px;
  }

  /* - Print styles: Increase text paragraph margin to make article more readable. */
  .article-body p {
    margin-top: 1em;
  }

  /* - Print styles: Increase text paragraph margin to make article more readable. */
  .article .author {
    margin-top: 1.5em;
  }

  /* - Print styles: Change page width to scale based on the screen size. */
  #container,
  #content {
    width: auto;
  }



  /* - Print styles: Hide unneeded page elements for cleaner printed output. */
  .top-bar,
  #container_navi,
  #container_vasen,
  #container_oikea,
  #js-recommended-toaster,
  .recommended-toaster,
  #tuoreimmat,
  .vaakaviiva,
  .fb-comments__outer-container {
    display: none !important;
  }

  /* - Print styles: Remove unneeded margin and border from the text container. */
  #container_keski  {
    padding: 30px 40px !important;
    margin: 0;
    border: none;
    width: auto;
  }

}

}
