@-moz-document domain("mugenmonkey.com") {
/* AGENT_SHEET */

/* Background Colors */
html, body {
background: none repeat scroll 0% 0% rgb(22, 24, 28) !important;
}

.crow, .spellc, .effTitle, .cselector, #aropt, .drgon, #settings, #minilink, #texti, .chzn-single, .chzn-results, 
.desinfo div, .desinfo, select, .inner-ele, #savedia, .forced-modal, .buildList, .buildrow:hover, .featBuild:hover,
.analy, .weptypsss , .wepstat, #armorfind, #armor-exclist, .excrow:hover, .settings, #custom-sort-pop, #wepTypeSel,
.oger, .c-body, .c-footer, .shr-link, .shr-inp {
background: none repeat scroll 0% 0% rgb(40, 42, 46) !important;
border-color: rgb(55, 59, 65) !important;
color: rgb(197, 200, 198) !important;
}

#content, .result-selected, .group-result, #voters, .borders, .buildrow, .featBuild, ul.subnav li a, .wepstat.bold,
.excrow, .armor_exb, .no-results, .armor-sets {
background: none repeat scroll 0% 0% rgba(24, 26, 30, 1) !important;
border: 1px solid rgba(44, 46, 50, .5) !important;
}

#lheader {
background: none repeat scroll 0% 0% rgba(40, 42, 46, 0.7) !important;
box-shadow: 0px 1px 2px rgba(0, 0, 0, 0.15) !important;
}


/* Text */
label, .chzn-single span, .rnk span, h3, li, strong, sup, .overit, #voters div, .fcancel, .fbuil, .wepTyp { color: rgb(197, 200, 198) !important }
    .bold { font-weight: 200 !important;}

/* Faded Text */
#loadUsage, .titles, .tclos, .rcop, p, .black, .ytitle { color: rgba(197, 200, 198, .6) !important; }


/* Inputs */
input, textarea, button, .login_form, .login.hover.intent, .fbuil, #custom-sort { 
background:none repeat scroll 0% 0% rgb(40, 42, 46) !important;
border:1px solid rgb(29,31,33) !important;
color: rgb(197, 200, 198) !important;
transition: background 0.2s ease 0s;
}

input:hover, textarea:hover, button:hover, .formbox:hover, .chzn-single:hover, .active-result:hover, .inner-ele:hover, 
.weptypsss div:hover, #custom-sort:hover, .fbuil:hover, .h:hover { 
background:rgb(56,58,62)!important;transition:background .3s
}

input:focus, textarea:focus, button:focus { 
border: 1px solid rgb(129, 162, 190) !important;
}

    /* Checkboxes */
input[type="checkbox"] {
    display: inline-block !important;
    height: 12px !important;
    position: relative !important;
    vertical-align: top !important;
    width: 12px !important;
    border-radius: 2px !important;
    border: 1px solid rgb(29, 31, 33) !important;
    background: none repeat scroll 0% 0% rgb(60, 62, 66) !important;
    transition: background 0.2s ease 0s !important;
    color: rgb(197, 200, 198) !important;
    -webkit-appearance: none;
    -moz-appearance: none;
}

input[type="checkbox"]:checked:before {
    background: url("data:image/png;base64,") no-repeat scroll center -8px transparent !important;
}

    /* Radio Buttons */
input[type="radio"] {
    display: inline-block !important;
    height: 12px !important;
    position: relative !important;
    vertical-align: top !important;
    width: 12px !important;
    border-radius: 5px !important;
    border: 1px solid rgb(29, 31, 33) !important;
    background: none repeat scroll 0% 0% rgb(60, 62, 66) !important;
    transition: background 0.2s ease 0s !important;
    color: rgb(197, 200, 198) !important;
    -webkit-appearance: none;
    -moz-appearance: none;
}

    /* Dropdowns */
.chzn-single, .chzn-drop, .inner-forced { border-color: rgb(50, 50, 50) !important; border-radius: 0px !important }
.chzn-search, .chzn-drop, .search { background-color: rgb(55, 59, 65) !important }
.chzn-single-with-drop { box-shadow: none !important }

select { -webkit-appearance: none; -moz-appearance: none; border: 1px solid rgb(68, 68, 68) !important }


/* Colors */
a, .blue, h2, span[style="color: rgb(125, 86, 58);"] { color:#81a2be !important }
a:hover, .red, .tclos:hover, span[style="color:#bb1b17"], span[style="color: rgb(181, 43, 43);"], select[style="color: rgb(221, 91, 91);"] { color:#cc6666 !important }
.deepred { color: #d03d3d !important }
.green, em { color: #8abeb7 !important }
span[style="color: rgb(53, 44, 44);"], span[style="color: rgb(136, 48, 210);"] { color: rgb(178, 148, 187) !important }
span[style="color: rgb(181, 157, 43);"] { color: #c2c87d !important }
span[style="color: rgb(58, 102, 125);"] { color: #538eac !important }


/* Background Images */
.spell2box { border: 0px solid rgb(55,59,65) !important; background: url(data:image/png;base64,) no-repeat scroll center center rgb(40, 42, 46) !important }
.weapons { border: 0px solid rgb(55,59,65) !important; background: url(data:image/png;base64,) no-repeat scroll center center rgb(40, 42, 46) !important }

/* Arrows */
.arrow.down, .arrow.down_fake, .vdo { background: url("data:image/png;base64,") no-repeat scroll 0% 0% transparent !important }
.arrow.up, .arrow.up_fake, .vup { background: url("data:image/png;base64,") no-repeat scroll 0% 0% transparent !important }
.arrow.down:hover, .arrow.down_fake:hover, .vdo:hover { background: url("data:image/png;base64,") no-repeat scroll 0% 0% transparent !important }
.arrow.up:hover, .arrow.up_fake:hover, .vup:hover { background: url("data:image/png;base64,") no-repeat scroll 0% 0% transparent !important }
.votearrows { width:15px !important }


/* Misc Formatting */
.active-result { border-bottom: 1px solid rgba(55, 59, 65, .8) !important }
em { background-color: transparent !important }
.buildrow2 { border-bottom: none !important }
#fillup { border-top: none !important }
#armorlist { border:none !important }
.sectitle { border-bottom: none !important }
.weapons { padding-left: 2px !important; padding-right: 2px !important }
ul.subnav li a:hover { background: none repeat scroll 0% 0% rgb(40, 42, 46) !important; border-color: rgb(55, 59, 65) !important }
.exwide { margin-right: 1px !important; margin-left: 1px !important }
hr { border-color: rgba(55, 59, 65, .8) !important }
.wepTyp { background: none repeat scroll 0% 0% rgba(24, 26, 30, 1) !important }
.wepTyp:hover, .armor-sets:hover { background: none repeat scroll 0% 0% rgb(30, 32, 36) !important; }
.wepArr1, .wepArr2 { display:none !important }


/* Scrollbars */
scrollbarbutton { display: none !important; }
::-webkit-scrollbar { width: 10px !important; height: 10px !important; }
::-webkit-scrollbar-thumb { border: 1px solid; }
::-webkit-scrollbar-track-piece:vertical { border-left: 0; }
::-webkit-scrollbar-track-piece:horizontal { border-top: 0; }
scrollbar, scrollcorner { -moz-appearance: none !important; width: 10px !important; height: 10px !important; }
scrollbar[orient='vertical'] { border-left: 0; }
scrollbar[orient='horizontal'] { border-top: 0; }
thumb { -moz-appearance: none !important; border: 1px solid; }
::-webkit-scrollbar-thumb:hover { background: #333; }
::-webkit-scrollbar-thumb:active { background: #111; }
::-webkit-scrollbar-track-piece, ::-webkit-scrollbar-corner, ::-webkit-scrollbar-resizer { background: #333; }
::-webkit-scrollbar-thumb { background: #1D1F21; border: #444 !important; }
scrollbar, scrollcorner { background: #1D1F21 !important; }
thumb { border-color: #444 !important; background: #1d1f21; }
thumb:active { background: #111 !important; }
thumb:hover { background: #313131; }

body {

background-attachment: scroll ;

}

}
