css:
@-moz-document url-prefix("http://bbs.jjwxc.net/"), url-prefix("http://bbs.jjwxc.com/"),url-prefix("https://bbs.jjwxc.net/"),url-prefix("https://bbs.jjwxc.com/") {

/* 
Author: Wayzlin
Version: 1.3
Update Date: 2020.03.14
*/


/*屏蔽广告*/
table[width="760"][align="center"][cellspacing="0"] div[align="right"],
font[color="gray"][size="-1"],
p[style="text-align:center"],
font[color="red"][size="-1"],
font[color="gray"][size="-2"],
table[width="790"][border="0"][align="center"][bgcolor="#E8F3FF"] img[src="img/jiantou.gif"],
table[width="790"][border="0"][align="center"][bgcolor="#E8F3FF"] [href="admin.php?board=3"],
body > table:nth-child(4) > tbody > tr:nth-child(4),
[id^=div-gpt-ad-],
body > center:nth-child(n) > font > table:nth-child(n) > tbody > tr > td:nth-child(2),
body > font:nth-child(n) > center > table:nth-child(n) > tbody > tr > td:nth-child(2),
body > font:nth-child(738) > center > table:nth-child(106),
table[width="20%"][border="0"][align="center"][cellpadding="0"][cellspacing="0"],
div[style="float:right;position:relative;width:650px;"],
#aswift_0_expand,
#aswift_1_expand {
    display: none !important;
}
div[style="width:760px;height:25px;margin:0 auto;position:relative;border:1px solid #666666;"] div[style="float:right;position:relative;width:650px;"],
table[width="800"][border="0"][align="center"][cellpadding="0"][cellspacing="0"][bgcolor="#999999"] {
    visibility: hidden !important;
}


/*美化*/
/*更改字体*/
* {
    font-family: 微软雅黑!important;
}

/*字体颜色*/
font[color="99CC00"] {
    color: #FFF !important;
    font-family: 幼圆!important;
    font-size: 15px;
    text-shadow: 0px 0px 2px #E9E6E8;
}
hr {
    color: #E8E8E8 !important;
    background-color: #FFF !important;
    height: 1px !important;
}
#form1 > span {
	color: #069 !important;
}
a:visited,
a:link,
#msglist > tbody > tr:nth-child(n) > td:nth-child(n) > table > tbody > tr > td > span {
	color: #1B8837 !important;
}
a:hover {
    color: #A65146 !important;
}

/*背景颜色*/
body[bgcolor="#FFE7F7"],
#msglist {
    background: linear-gradient(0deg, #A4C0B4 0%, #B2D5C1 50%,#B2D4B9 100%);
}
#msglist > tbody > tr:nth-child(n),td[bgcolor="#E8F3FF"],
#selecthead > td:nth-child(n) {
    background: rgba(255, 255, 255, 0)!important;
}
#msglist > tbody > tr:nth-child(n) > td:nth-child(n) {
    border: solid #fff 1.5px;
}

}
