css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("paypal.com") {

input, select, textarea, .js_transactionDetails,
.selectedFundingOption-details, .transactionAmount,
.activityModule .transactionItem, .body, #miniCart,
.head, .miniCartBody, .navbar-inner, #control,
.login, body, .toggle, #expander, #purchase-detail, 
.collapsed, #navPrimary ul, #navPrimary li, .social-box
{
background: #000000 !important; 
}
#header, #contents, #content, .novideo-bg,
.parallax-bg
{
background: #111111 !important; 
}
.vx_btn, .vx_btn-small, .vx_btn-secondary,
.walletModule, .activityModule,
.card,
.button, .actionContinue, .btn, .btn-small
{
background: #016E99 !important; 
}
html, body,
.mainBody, #body .row-fluid, header div,
#hero div, footer, .global-footer
{
background-color: #111111 !important; 
}
.messageBox, .notification
{
background-color: #000000 !important; 
}
body,
body#login div#control
{
border:none !important; 
}
.transactionRow:nth-child(2n) .transactionAmount::before, .installmentRow:nth-child(2n) .transactionAmount::before, .transactionRow.year-title:nth-child(2n) .transactionAmount::before, .installmentRow.year-title:nth-child(2n) .transactionAmount::before, .transactionRow:nth-child(2n) .installmentSummary .balance::before, .installmentRow:nth-child(2n) .installmentSummary .balance::before, .transactionRow.year-title:nth-child(2n) .installmentSummary .balance::before, .installmentRow.year-title:nth-child(2n) .installmentSummary .balance::before,

.transactionRow:nth-child(2n+1) .transactionAmount::before, .installmentRow:nth-child(2n+1) .transactionAmount::before, .transactionRow.year-title:nth-child(2n+1) .transactionAmount::before, .installmentRow.year-title:nth-child(2n+1) .transactionAmount::before, .transactionRow:nth-child(2n+1) .installmentSummary .balance::before, .installmentRow:nth-child(2n+1) .installmentSummary .balance::before, .transactionRow.year-title:nth-child(2n+1) .installmentSummary .balance::before, .installmentRow.year-title:nth-child(2n+1) .installmentSummary .balance::before
{
background-image:none !important; 
linear-gradient:none !important; 
}
.navbar-header
{
border-bottom:1px #111111 solid !important; 
}
body, label, input, select, b, a, p, dd, dt, strong, textarea,
h1, h2, h3, h4, h5,
#header-buy, #header-sell, #header-send, #header-business,
.header, .navText, .transactionDescription, .hero-subheadline,
.dateDay, .copy, .headline, .medium, .hero-headline, header,
.balanceNumeral span, .currenciesList, .currenciesEntry,
.fiModule-fiType, .recipientAmountWrapper, .conversionRate,
.vx_small-text, .nemo_fiModule-acctDetails, .senderPay,
.selectedFundingOption-details, .netAmount, .isNegative,
.small, .head, .secondary, .amount, .heavy, .subhead,
.help, .inset, .confidential, .altAmount, .altHeader,
.buyerEmail, .review, .cokiee-legal, .hero-hatch,
.footer, li, .toggle, #header, .secondary,
div#header h1, #xptContentMain div,
#expander, #purchase-detail, .collapsed
{
color: #FFFFFF !important; 
}
.navText:hover, a:hover
{
color: #009CDE !important; 
}
.navText, .footer
{
text-shadow: none !important; 
}
.navbar-header
{
box-shadow:none !important; 
}
}
