css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("diasp.eu"),
domain("diaspora-fr.org"),
domain("framasphere.org"),
domain("diasp.org"),
domain("joindiaspora.com"),
domain("pod.geraspora.de"),
domain("diasp.de"),
domain("despora.de"),
domain("calispora.org"),
domain("poddery.com"),
domain("londondiaspora.com"),
domain("wk3.org"),
domain("www.cryptospora.net"),
domain("ser.endipito.us"),
domain("ilikefreedom.org"),
domain("yaspora.com"),
domain("diasp.eu.com"),
domain("nesc.io"),
domain("pod.nocentre.net"),
domain("failure.net"),
domain("diaspora.re"),
domain("dipod.es"),
domain("social.deuxfleurs.fr"),
domain("podricing.org") {

/*****************************************************************************************
*
*    				Header
*
*******************************************************************************************/
header{background:#333 !important;box-shadow:none !important;}
body > header .diaspora_header_logo{display:none !important;}
body > header #global_search form input{border-radius:0px !important;}
body > header #nav_badges .badge .badge_count{font-size: 1.4em !important;background:transparent !important;}
/*****************************************************************************************
*
*					Body
*
*******************************************************************************************/

.span-24{width: 100% !important;}
.container{width: 100% !important;}
.rightBar{margin-top: 180px !important;}
.stream_container{border-left: none !important;}
.reshare{border-left:3px solid #000 !important;border-radius:0px 5px !important;}
.reshare>.media:before{content:'';display: block !important;position:absolute !important;margin-left:-60px !important;margin-top: 30px !important;background: url('http://sanspseudofix.fr/wp-content/uploads/2013/05/reshare_24_bold.png') no-repeat !important;width:24px !important;height:24px !important;/*background-position:-35px -35px !important;*/border-radius: 24px !important;}
.feedback .reshare{border-left: none !important;}

/*****************************************************************************************
*
*					Navigation (right)
*
*******************************************************************************************/

.span-5,.span-6{float: right !important;width: 16% !important;display: inline-block !important;margin-top: 100px !important;margin-left: 20px !important;}

#leftNavBar{
    float: right !important;
    display: inline-block !important;
    margin-top: 130px !important;
    width: 15% !important;
    padding: 10px !important;
}

.rightBar{
    width:15% !important;
    margin-top: 100px !important;
    color: #E9E4CA !important;
}


#back-to-top{border-radius:50% !important;}

/*****************************************************************************************
*
*					Contenu Principal (milieu)
*
*******************************************************************************************/

.span-13,.span-18{width: 60% !important;margin-top:-19px !important;}


/*************************** Fixed publisher */

#publisher{
	position: fixed !important;
	display: block !important;
	width: 36% !important;
	max-height: 85% !important;
	right: 0px !important;
	top: 39px !important; /* 59 */
	background: #fff !important;
    border-bottom:none !important;
}
.stream .stream_element{margin-top: 50px !important;border-bottom:none !important;border-top:none !important;padding-top:3px !important;/*box-shadow:1px 2px 1px #333,-1px 0px 1px #333 !important;*/}
.stream .stream_element:first-child{margin-top: 0px !important;}
.span-13 .stream .stream_element:after{
    content:"";
    position:absolute !important;
    background: none repeat scroll 0 0 #303030;
    border-radius: 50% 50% 50% 50%;
    box-shadow: 30px 0 #2072A7, -30px 0 #2072A7;/* 2072A7 */
    height: 14px;
    left: 33%;
    transform: translateX(-50%);
    width: 14px;
}
#publisher #status_message_fake_text{
    max-height: 400px !important;
    resize: vertical !important;
    overflow: visible !important;
}


/*************************** End fixed publisher */
#publisher .button, #publisher .dropdown .wrapper{
background-color: #28616A !important;font-weight:bold !important;border-radius: 5px !important;border-bottom:3px solid #2A494C !important;border-left:3px solid #2A494C !important;border-top: none !important;border-right: none !important;background-image: none !important;color: #FFF !important;}
#publisher .button:hover,#publisher .button:active{background-color: #2A494C !important;}
#publisher .button.creation{background-color: #C94C46 !important;border-bottom: 3px solid #963934 !important;}
#publisher .button.creation:hover,#publisher .button.creation:active{background-color:#963934 !important;}

.comment{border-bottom: none !important;}
.comment.media{border-top: none !important;}
.comments .comment .bd,
#single-post-interactions .comments .comment .bd{background: #fff !important;border:1px solid #000 !important;padding: 5px !important;}
.comment_stream .comments .comment:before{content:"";display:block !important;position:absolute !important;left:145px !important;border-right:11px solid #333 !important;border-top:10px solid transparent !important;border-bottom:10px solid transparent !important;}
#single-post-interactions .comments .comment:before{content:"";display:block !important;position:absolute !important;left:70px !important;border-right:10px solid #333 !important;border-top:10px solid transparent !important;border-bottom:10px solid transparent !important;}
#author_info .comments .comment:before{content:"";display:block !important;position:absolute !important;left:121px !important;border-right:10px solid #333 !important;border-top:10px solid transparent !important;border-bottom:10px solid transparent !important;}
#main_stream .comment-content blockquote{border-left: 3px solid #000 !important;padding-left: 5px !important;}    
.stream_element .collapsible p{color: #333 !important;}

.collapsible.comment-content > p{color: #333 !important;}

.likes img{background: none !important;padding: 0 !important;}

.avatar.small{
	padding: 4px !important;
	background: #fff !important;
	border:1px solid #000 !important;
        border-radius: 50% !important;
}
/*********************************
Profile
**********************************/

.span-18 .comments .comment .bd{background: #fff !important;border:1px solid #000 !important;padding: 5px !important;}

/*********************************
Tags page
**********************************/
    
div[data-template="tag_following_action"]{margin-top: 140px !important;}
#tags_show .comment_stream .comments .comment:before{display:none !important;}

}
