@namespace url(http://www.w3.org/1999/xhtml);


/* build: 20.01.2014 - 16:27 */

@-moz-document domain("raidrush.ws") {
  
  /* build: 20.01.2014 - 16:27 */


/* --- hacks & fixes --- */

/* die profil-bearbeitung abschalten */
#userprof_customize_edit::after {
  content: "Diese Funktion steht derzeit nicht zur Verfügung";
  color: #fff;
  text-shadow: 0 1px #000;
  font-size: 20pt;
  display: block;
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  text-align: center;
  background: rgba(100, 100, 100, .8);
}

/* den "profildesign" button richtig platzieren */
#profile_customization_button {
  position: absolute !important;
  white-space: nowrap !important;
  top: -32px !important
}

/* nur wenn die bearbeitungsfunktion sichtbar ist ein margin drunter setzen */
#userprof_customize:not([style*="height: 0px"]) {
  margin-bottom: 30px !important;
}

/* "was ist neu" sidebar auf der startseite
   tab-layout anpassen */
#whatsnewsidebar div.blocksubhead.smaller {
  font-size: 8pt !important;
  height: 30px !important;
}

#whatsnewsidebar div.blocksubhead.smaller li {
  width: auto !important;
}

#whatsnewsidebar div.blocksubhead.smaller a,
#whatsnewsidebar div.blocksubhead.smaller span {
  line-height: 20px !important;  
  height: 20px !important;
  white-space: nowrap;
  padding-left: 8px !important;
  padding-right: 8px !important;
}

/* die mitgliederliste innerhalb von gruppen anpassen */
#membersblock ul.blockrow li {
  margin-right: 22px !important;
}

#membersblock ul.blockrow li:nth-child(4n) {
  margin-right: 0 !important;
}

#membersblock ul.blockrow li:nth-child(4n)::after {
  content: " ";
  height: 20px;
  display: block;
  clear: both;
}

/* diese meisterwerke findet man im "thumbs up? center" */
table[width="100%"] tr:not([valign="top"]) td {
  padding: 6px !important;
  border-top: 1px solid #ccc;
}

/* ohne worte ... */
big {
  font-size: inherit;
}

/* dunkle icons auf dunklem hintergrund sind eher suboptimal */
.blockhead > img[src*="/site_icons/"],
.blockheadinfo + img {
  position: relative !important;
  background: #fff !important;
  border-radius: 2px;
  padding: 0 1px;
}

/* --- general --- */

body {
  min-width: 0 !important;
  max-width: none !important;
  margin: 0 !important;
  padding: 0 !important;
}


.header {
  text-transform: none !important;
}

.newcontent_textcontrol, 
*:not(.blocksubhead):not(li) > .textcontrol:not(#tag_edit_link),
#sidebar_container .actionbutton_container a.pagetitleinfo.textcontrol {
  background-color: transparent !important;
  background-image: linear-gradient(to bottom, #444, #222) !important;
  color: #fff !important;
}

.newcontent_textcontrol:hover, 
*:not(.blocksubhead):not(li) > .textcontrol:not(#tag_edit_link):hover,
#sidebar_container .actionbutton_container a.pagetitleinfo.textcontrol:hover {
  background-image: linear-gradient(to bottom, #666, #222) !important;
}

.newcontent_textcontrol,
.actionbutton_container a.pagetitleinfo.textcontrol {
  font-size: 10pt !important;
  font-weight: bold !important;
  padding-top: 0 !important;
  padding-bottom: 0 !important;
  line-height: 28px !important;
  text-shadow: 0 1px #000;
  border: 1px solid #111 !important;
  border-radius: 4px !important;
  box-shadow: 0 0 1px rgba(200, 200, 200, .8) inset,
              0px 3px 8px #DDD !important;
}

.newcontent_textcontrol span,
.actionbutton_container a.pagetitleinfo.textcontrol span {
  position: relative;
  top: 1px;
}

#pollinfo .blockhead > .textcontrol:not(#die_oberen_regeln_hebelt_diese_sonst_aus) {
  background-color: transparent !important;
  background-image: none !important;
}

#sidebar_container a.textcontrol:not(:hover) {
  color: #424242 !important;
}

li.sgicon .controls a.textcontrol {
  padding-left: 22px !important;
}

li.sgicon .controls a.textcontrol img { 
  left: 2px !important;
}

.above_body,
.body_wrapper,
.below_body {
  max-width: 1100px !important;
  margin-left: auto !important;
  margin-right: auto !important;
  position: relative;
  -moz-box-sizing: border-box;
       box-sizing: border-box;
}

.forumhead,
.blockhead,
.userprof_module,
#searchtypeswitcher li.selected a,
.activitystream_block dd.selected,
.searchlisthead,
.threadlisthead,
.posthead {
  background-image: linear-gradient(to bottom, #444, #222) !important;
  border-color: #111 !important;
  color: #fff !important;
  /* border-radius: 0 !important; */
}

#pollinfo .blockhead {
  background: transparent none !important;
}

/*
dd.userprof_module, 
dd.userprof_moduleinactive, 
dd.userprof_content, 
dd.userprof_module a, 
dd.userprof_moduleinactive a, 
dd.userprof_content a,
#searchtypeswitcher li a {
  border-radius: 0 !important;
}
*/

/* bringt die sidebar und den content auf eine höhe auf der startseite */
.blockfoot, .blocksubfoot {
  padding-top: 10px !important;
}

/* bei aktivitäten einen rahmen um die beiträge zeichnen */
li.activitybit {
  border: 1px solid #ccc !important;
}

/* die forenheader ein wenig dicker machen */
.forumhead {
  padding-top: 3px !important;
  padding-bottom: 1px !important;
  margin-top: 12px !important;
}

.forumbit_nopost .forumhead .forumtitle {
  min-width: 62% !important;
}

.forumbit_nopost .forumhead span.forumlastpost, 
.forumbit_post .forumhead span.forumlastpost {
  margin-left: 22px;
}

.forumbit_post .forumstats li, 
.forumbit_post .forumstats_2 li {
  white-space: nowrap !important;
}

#below_unread {
  padding: 6px !important;
  font-size: 8pt !important;
  text-shadow: 0 1px #fff !important;
}

/* ein/ausklapp funktion für foren entsprechend positionieren */
a.collapse {
  right: 14px !important;
  top: 22px !important;
}

.block a.collapse {
  top: 6px !important;
}

.blockhead a.collapse {
  top: 10px !important;
}

#pagetitle {
  border-bottom-color: #111 !important;
}

#userinfoblock a.avatar {
  padding: 5px !important;
}

#userinfoblock a.avatar .avatarcontainer img {
  outline: none !important;
  background-color: #f9f9f9 !important;
  border: 1px solid #ccc !important;
  padding: 10px !important;
  box-shadow: rgba(0, 0, 0, 0.2) 0px 0px 5px 0px !important;
}

.footer {
  background-color: #222 !important;
  border-color: #111 !important;
}

.footer_links {
  margin-top: 5px !important;
}

/* --- header --- */

/* der hintergrund ist ganz am ende der datei, 
   da ein png-bild via data-uri eingebettet ist */

#header::after {
  display: none !important;
}

.logo-image {
  color: transparent !important;
  display: inline-block !important;
  float: none !important;
  clear: none !important;
  margin-left: 10px;
  height: 100px !important;
}

.nouser #remember label {
  /* als "bold" im cms definiert -> machen wir es immer bold */
  font-weight: 700 !important;
  /* im cms global für labels definiert, also setzen wir es zurück */
  padding: 0 !important;
}

.toplinks ul.nouser .logindetails,
.toplinks ul.nouser > li > a {
  background-image: linear-gradient(to bottom, #666, #444) !important;
}

/* danke an Die_Quelle für den hinweis */
.toplinks ul.nouser .logindetails:hover,
.toplinks ul.nouser > li > a:hover,
.toplinks > ul > li > a:not(.popupctrl):hover {
  background-image: linear-gradient(to bottom, #888, #666) !important;
  border-radius: 0 0 5px 5px !important;
}

.toplinks a.popupctrl:hover,
.toplinks a.popupctrl.active {
  background-color: transparent !important;
  background-image: url("http://board.raidrush.ws/images/misc/arrow.png"),
                    linear-gradient(to bottom, #888, #666) !important;
  background-repeat: no-repeat, repeat !important;
  background-position: 100% 50%, 0 0 !important;
  border-radius: 0 0 5px 5px !important;
}

/* --- navigation --- */

#navbar {
  background: #222 !important;
  background-image: none !important; /* wtf ^^ */
  margin-top: 0 !important;
}

.navtabs {
  background: #111 !important;
}

.navtabs > ul.floatcontainer li:hover a.popupctrl,
.navtabs > ul.floatcontainer li:hover a.popupctrl.active, 
.navtabs > ul.floatcontainer li a.popupctrl.active {
  border-color: #111 !important;
  background-color: #666 !important;
}

.navtabs .popupbody {
  border-color: #111 !important;
  background-color: #444 !important;
}

.navtabs .popupbody li {
  border-color: #111 !important;
}

.navtabs .popupbody li:first-child {
  border: 0 none !important;
}

.navtabs ul {
  border-top: 1px solid #444 !important;
}

.navtabs li {
  border-right-color: #444 !important;
}

.navtabs li:first-child {
  border-left-color: #444 !important;
}

.navtabs li a.navtab {
  background: linear-gradient(to bottom, #666, #444) !important;
  border-top-color: #999 !important;
  border-left-color: #999 !important;
}

.navtabs li.selected a.navtab {
  box-shadow: 0 1px #CAFF2A inset, 0 2px #C4FF12 inset, 1px 0 #999 inset;
  border-top-color: #FAFFEA !important;
  border-left: 0 none !important;
}

.navtabs li a.navtab:hover,
.navtabs li.selected a.navtab {
  color: #fff !important;
  background: linear-gradient(to bottom, #888, #666) !important;
}

.toplinks .nonotifications .popupbody a:hover,
.navtabs > ul.floatcontainer .popupbody li a:hover {
  background: linear-gradient(to bottom, #fefefe, #eee) !important;
}

/* --- suchfunktion im header --- */ 

.globalsearch .textboxcontainer,
.globalsearch .textboxcontainer span {
  border: 0 none !important;
  padding: 0 !important;
} 

.globalsearch .textboxcontainer span {
  height: auto !important;
}

.globalsearch .buttoncontainer,
.globalsearch .buttoncontainer span {
  border: 0 none !important;
}

.globalsearch .buttoncontainer {
  position: relative !important;
  top: 1px !important;
}

.globalsearch input.textbox {
  border: 1px solid #111 !important;
  padding: 0 4px !important;
  background: #444 !important;
  height: 24px !important;
  color: #fff !important;
  width: 145px !important;
}

/* --- gruppen --- */

#groupsearch {
  border-width: 1px !important;
}

#groupsearch .searchbox {
  padding: 4px 0 !important;
}

#sidebar li.sgicon .leftcol img.sgicon {
  width: auto !important;
  height: auto !important;
  max-width: 50px;
  max-height: 50px;
}

#groupinfo  #sgiconbox {
  text-align: center;
}

#view-group-groupimage {
  display: inline-block !important;
  border: 1px solid #ccc;
  background: #f9f9f9;
}

/* --- die content + sidebar geschichte --- */

/* float generell erstmal abstellen */
#content_container {
  float: none !important;
}

#content_container,
#content_container #content {
  margin-right: 0 !important;
}

/* dieser selector bezieht sich in erster linie auf die forum-seite */
.body_wrapper > #sidebar_container {
  position: absolute;
  top: -18px;
  right: -335px;
  width: 325px !important;
  float: none !important;
}

/* falls die sidebar im original-style ausgeblendet wurde hier wieder einblenden */
.body_wrapper > #sidebar_container.sidebar_closed,
.body_wrapper > #sidebar_container #sidebar.sidebar_hidden {
  display: block !important;
}

/* den button zum ausblenden entfernen */
#sidebar_button {
  display: none !important;
  pointer-events: none !important;
}

/* bei einer breite von 1600 - 1790px das forum nur leicht nach links versetzen */ 
@media all and (min-width: 1600px) and (max-width: 1790px) {
  .above_body,
  .body_wrapper,
  .below_body {
    width: 980px !important;
    left: -50px !important;
  }
}

/* bei einer breite von 1342 - 1599px das forum ein wenig mehr nach links versetzen */
@media all and (min-width: 1342px) and (max-width: 1599px) {
  .above_body,
  .body_wrapper,
  .below_body {
    width: 980px !important;
    left: -165px !important;
  }
}

/* bei einer breite von <= 1341px die sidebar komplett entfernen */
@media all and (max-width: 1341px) {
  .above_body,
  .body_wrapper,
  .below_body {
    width: 980px !important;
    left: 0 !important;
  }

  .body_wrapper > #sidebar_container,
  .body_wrapper > #sidebar_container.sidebar_closed,
  .body_wrapper > #sidebar_container #sidebar.sidebar_hidden {
    display: none !important;
  }
}
  
/* <body onpageshow="..." ...> kommt bei der detailansicht des cms vor, 
   hier entfernen wir die sidebar */
[onpageshow] .vbcms_content .yui-sidebar {
  display: none !important;
}

[onpageshow] .yui-tvb-r4 #yui-main .yui-b {
  margin-right: 0 !important;
}

/* und jetzt fälle bei denen wir die sidebar lassen wo sie vorher war :-) */

.body_wrapper #grouppagetitle ~ #content_container {
  float: right !important;
  margin-left: -345px !important;
}

.body_wrapper #grouppagetitle ~ #content_container #content {
  margin-left: 345px !important;
}

.body_wrapper #grouppagetitle ~ #sidebar_container {
  position: static !important;
  float: left !important;
  padding-top: 2px !important;
}

.body_wrapper #above_content ~ #content_container,
.body_wrapper #usercss #content_container { 
  float: left !important;
  margin-right: -345px !important;
}

.body_wrapper #above_content ~ #content_container #content,
.body_wrapper #usercss #content_container #content {
  margin-right: 345px !important;
}

.body_wrapper #above_content ~ #sidebar_container,
.body_wrapper #usercss #sidebar_container {
  position: static !important;
  float: right !important;
  padding-top: 8px !important;
}

.blog #content_container {
  margin-top: 10px !important;
}

/* --- cms --- */

/*
.vbcms_content .yui-b .cms_widget .widget_content {
  padding: 5px !important;
}
*/

/* --- erweiterte suche --- */  

#searchtypeswitcher li a {
  background: #F1F1F1 url('http://board.raidrush.ws/images/gradients/top-highlight.png') repeat-x !important;
  border: 1px solid #C1C1C1 !important;
  border-bottom: 0 none !important;
  color: #424242 !important;
  font-weight: bold !important;
}

#searchtypeswitcher {
  border-bottom-color: #222 !important;
}

.formcontrols,
.blockbody.settings_form_border {
  border-color: #222 !important;
}

#sidebar_container .formcontrols {
  border-color: #dadada !important;
}

/* --- forenansicht --- */

.announcements .announcerow,
.announcerow .announcement,
.announcerow .announceinfo {
  background-color: #eee !important;
}

/* --- themenansicht (posts) --- */

#pagetitle .threadtitle {
  margin-top: 10px !important;
}

.postdetails,
.postdetails .userinfo {
  background-color: #f5f5f5 !important;
}

.postdetails .userinfo {
  text-align: center;
}

.postbitlegacy .userinfo a.username {
  font-size: 12pt !important;
}

/*
.postdetails .username_container {
  text-indent: 22px;
}
*/

.postdetails img.onlinestatus {
  position: relative !important;
  margin-right: -20px !important;
  top: 4px !important;
  right: auto !important;
}

.postdetails .userinfo > div[style*="width:102px"] {
  margin: 0 auto !important;
  margin-bottom: 2px !important;
}

.postdetails .userinfo .postuseravatar {
  float: none !important;
  text-align: center !important;
  padding: 10px !important;
  border: 1px solid #ccc !important;
  background-color: #f9f9f9 !important;
  box-shadow: 0 0 5px rgba(0, 0, 0, .2);
  margin-top: 10px !important;
}

.postbitlegacy dl.userinfo_extra,
.postbitlegacy .userinfo .userinfo_extra {
  float: none !important;
}

.postdetails .userinfo .userinfo_extra {
  border: 1px solid #ccc;
  background-color: #f9f9f9 !important;
  box-shadow: 0 0 5px rgba(0, 0, 0, .2);
}

.postdetails .userinfo .userinfo_extra dt {
  padding: 4px 6px !important;
  font-weight: bold;
}

.postdetails .userinfo .userinfo_extra dd {
  border-bottom: 1px solid #ccc;
  padding: 4px 6px !important;
  text-align: right;
}

.postdetails .userinfo .userinfo_extra table td {
  padding: 4px 6px !important;
  text-align: left;
} 

.postdetails .xperience {
  margin-top: 10px !important;
}

.postbitlegacy dl.userinfo_extra, 
.postbitlegacy dl.user_rep {
  width: auto !important;
}

.usertitle.understate {
  white-space: nowrap !important;
}

.usertitle.understate br {
  display: none;
}

.usertitle.understate b {
  display: inline-block;
  margin-right: 10px;
}

.postbody .title {
  padding-top: 1px !important;
  font-size: 10pt !important;
}

.postbody .title.icon img {
  position: relative;
  top: 2px;
}
 
/* --- seitenwahl --- */

.pagination span a {
  border-color: #ccc !important;
  background: linear-gradient(to bottom, #fefefe, #eee) !important;
  color: #000 !important;
  text-shadow: 0 1px #fff;
  box-shadow: 0 0 1px rgba(255, 255, 255, .8) inset;
  padding: 2px 8px !important;
}

.pagination span.prev_next,
.pagination span.first_last {
  position: relative;
  top: 1px;
}

.pagination span.prev_next img,
.pagination span.first_last img {
  position: relative !important;
  top: 2px !important;
}

.pagination span a.popupctrl {
  background-color: transparent !important;
  background-image: url('http://board.raidrush.ws/images/misc/black_downward_arrow.png'),
                    linear-gradient(to bottom, #fefefe, #eee) !important;
  background-repeat: no-repeat, repeat !important;
  background-position: 0 50%, 0 0 !important;
  padding-left: 18px !important;
}

.pagination_top {
  width: 70% !important;
}

/* --- profilseite --- */

/* stats in der sidebar */
.member_summary dl.stats dt {
  white-space: nowrap !important;
  font-weight: bold !important;
  font-size: 8pt !important;
  padding: 8px 4px !important;
}

.member_summary dl.stats dd {
  margin-left: 0 !important;
  float: none !important;
  clear: none !important;
  text-align: right !important;
  border-bottom: 1px solid #eee;
  width: auto !important;
  padding: 6px 4px !important;
}

.member_summary dl.stats:last-child dd {
  padding-top: 0 !important;
}

.member_summary dl.stats:last-child dd {
  border-bottom: 0 none !important;
  padding-bottom: 0 !important;
}

.member_summary dd.avatar img {
  margin-top: 6px !important;
}

/* ist jetzt online ... */
.member_summary dl.stats:nth-child(2) dt {
  float: none !important;
  display: block !important;
  padding-bottom: 0 !important;
}

.member_summary dl.stats:nth-child(2) dd {
  text-align: left !important;
}

/* horizontale trennlinie zwischen tabs */
.memberprofiletabunder {
 background-image: none !important;
 background-color: #222 !important;
}
     
/* ein wenig abstand zwischen tabs */
.tabslight dd, 
.as-tabs dd {
  margin-left: 2px !important;
}

.tabslight dd:first-child,
.tabslight dt + dd,
.as-tabs dd:first-child,
.as-tabs dt + dd {
  margin-left: 0 !important;
}

#activity_tab_container > div {
  padding: 2px !important;
}

/* ------------------------------ */

.above_body {
  background-image: url("data:image/png;base64,"),
                    linear-gradient(to bottom, #666, #222) !important;
}


/* Vorschläge von Die_Quelle 4 RR */
/* TODO: diese deklarationen mit den bereits vorhandenen vereinen */ 

/* Navbar/tabs Hintergrund entfernt */
.navtabs {
  background: none !important;
}

/* Benutzerprofil - Runde Ecken entfernt */
/* TODO: @Die_Quelle -> ist noch inkonsistent, bitte überarbeiten! */
/*
#sidebar_container .blockbody {
  border-radius: 0 !important;
}
*/

/* Benutzerprofil - Moderator Link - linksbündig */
.summaryinfo .user {
  text-align: left !important;
}

/* Benutzerprofil - Left Menu Hover color */
#usercp_nav .blockrow * a:hover {
  background-color: #CAFF6C !important;
}

#usercp_nav .blockrow * a:active {
  background-color: #CAFF6C !important;
}

/* Mein Profil - Rang Icons Abstand nach oben */
#sidebar_container.member_summary .mainblock .rank {
  margin: 5px 0px 0px 0px !important;
}

/* Mein Profil - Userinfo abstand angleichen */
#sidebar_container.member_summary h1 {
  position:relative !important;
  left:7px !important;
}

/* Seiten Auswahl im Blog/(forum?) */
.pagination span.prev_next, .pagination span.first_last {
  position: relative;
  top: 0px !important;
}

/* Blog - Kategorien Abstand angepasst */
.category_widget .active_menuitem a, .category_widget .main_menuitem a {
  padding-left: 0.3em;
}

/* Blog - Avatar Abstand */
.postbit-lite .postbit_pb_left {
  padding: 0px 10px 0px 10px !important;
}

/* Blog - Avatar Maximal Breite */
.postbit-lite .avatarlink img {
  max-width: 60px !important;
}

/* Blog - Kommentare */
.postbit-lite .comment_text {
  min-height: 50px !important;
}

/* Blog - Abstand zwischen Meta und Bloginhalt, Border zur Verbesserung */
.blogmeta {
  margin-top: 20px !important;
  border-top: 1px solid #ccc !important;
  padding-top: 10px !important;
}
  
}
