css:
/* 420chan - Fuzzy Grass */
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain(420chan.org) {

html { background-color:rgb(31,31,31) !important; }

* a { color:rgb(125,172,74) !important; text-decoration:none !important; }
* a:hover { color:rgb(168,168,168) !important; text-decoration:none !important; }

.leaderblord,
li .boardicon,
img.board_icon,
#derps,
form#searchform,
.boardlink .icons,
#delform .banned,
.brand-mamabar-wrapper,
#taimaba-featured,
#content_box #sidebars,
td.doubledash,
.blarbl,
#gritter-notice-wrapper,
.tooltip,
span.omittedposts a,
div[style="margin: 5px auto 1px; width: 728px;"],
div[style="width: 100%; text-align: center; font-size: 18px; font-weight: bold; line-height: 22px; margin: 5px auto"]
{ display:none !important; }

.pagelist a:before { content:"[" !important; }
.pagelist a:after { content:"]" !important; }

hr { opacity:0 !important; }

a.postref { background:none !important; border:0 !important; }

#board_logo { float:none !important; }

#content_box #content { width:100% !important; }
#content_box #content .teaser { width:47% !important; }

#postform tr[style="font-weight: bold;"] { display:none !important; }

#delform div table { width:100% !important; }

blockquote, .unkfunc, .thread_header, .replyheader, .omittedposts, #postform *, .filesize *
{ font-size:13px !important; }

.replyheader .reflink a { color:rgb(170, 170, 170) !important; }
.replyheader .reflink a:hover { color:rgb(200,200,200) !important; }

#content, #footer { background-color:rgb(28,28,28) !important; }

td.reply {
   background-color:rgb(35,35,35) !important;
   border:0 !important;
}
.unkfunc { font-weight:normal !important; color:rgb(120, 153, 34) !important; }
.filetitle { color:rgb(170,170,170) !important; text-shadow:none !important; font-size:13px !important; }
td.reply ol, .opcomment ol { padding-left:7px !important; }

.replyheader { background:none !important; }
.replyheader * a, ul.dropdown * a { color:#ddd !important; }
.replyheader * a:hover, ul.dropdown * a:hover { color:#fff !important; }

#dropdown_container { margin-top:0 !important; }
.brand-mamabar { height:0px !important; }

.commentpostername, .postername { color:rgb(124, 45, 45) !important; }
span.postertrip { color:rgb(62, 113, 87) !important; font-weight:bold !important; font-size:13px !important; }

#second_nav * a { color:#fff !important; }
#second_nav * a:hover { color:rgb(168,168,168) !important; }

.boardlink { padding-left:5px !important; }
.boardlink:hover, #board_logo a { color:#fff !important; }

a.fg-button, .reflink a { background:none !important; color:#ddd !important; }
a.fg-button:hover, a.fg-button:active, .reflink a:hover { background:none !important; color:#fff !important; }

.filesize, .thumbnailmsg { color:rgb(128, 128, 144) !important; font-style:italic !important; }

p.board_title_block { float:none !important; }

#rules ul { text-align:center !important; }
#rules ul li { display:none !important; }
#rules ul li[style="font-size: 16px; font-weight: bold;"] { display:inline !important; }

#active-discussions * { font-size:11px !important; }
.sub .boardlink, li.second_nav_one .sub a { font-size:13px !important; }

#header, #board_logo { text-align:center !important; }

#postform input, #postform textarea {
   -moz-appearance:none !important;
   border:0 !important;
   background-color:rgb(35,35,35) !important;
   color:#ddd !important;
}
#postform input[type="text"], #postform textarea {
   padding:3px !important;
}
#postform input[type="submit"] {
   padding:2px !important;
}
input[type="file"] { width:75% !important; height:21px !important; }

#postform table {
   width:45% !important;
   border-spacing:0px !important;
   border-collapse:collapse !important;
}

#postform textarea { width:100% !important; }
#postform input[type="text"] { width:74% !important; }
#postform input[type="submit"] { width:24% !important; float:right !important; }

#postform input[name="field1"] { width:100% !important; }
.replypage #postform input[type="submit"] { width:100% !important; }

}
