@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.mtsinaishul.com") {
#header {
    display:none;
}
#content {
    width: auto !important;
}
#sidebar_left {
    display: none;
}
#template_slider {
    display: none;
}
#maincontent {
    display: none;
}
#sidebar_right {
    float: none !important;
}   
#sidebar_right .content_container .right_bm:first-child {
    display: none;
}
   
.fck_widget_calendar, .fck_widget_calendar td {
    text-align: center;
}
#sidebar_right .right_bm table td {
    text-align: center;
}
#sidebar_right .right_bm:nth-child(4), 
#sidebar_right .right_bm:nth-child(5) {
    display: none;
}
#footer {
    display: none;
}
#sidebar_right .right_bm table td {
    text-align: center !important;
    font-size: 18px !important;
}


 

}
