css:
* {
margin: 0;
padding: 0;
border: 0;
outline: 0;
line-height: 1;
vertical-align: baseline;
font: inherit;
}
  
html {
margin: 0;
padding: 0;
border: 0;
outline: 0;
background-color: darkslategray!important;
background-image: -webkit-linear-gradient(45deg, rgba(147, 179, 179, 0.45) 45%, rgba(0, 0, 0, 0) 45%), -webkit-linear-gradient(-135deg, rgba(147, 179, 179, 0.55) 35%, rgba(0, 0, 0, 0) 35%), -webkit-linear-gradient(-45deg, rgba(147, 179, 179, 0.65) 35%, rgba(0, 0, 0, 0) 35%), -webkit-linear-gradient(225deg, rgba(147, 179, 179, 0.45) 45%, rgba(0, 0, 0, 0) 45%)!important;
background-size: 5PX 5PX, 3PX 3PX, 5PX 5PX, 3PX 3PX;
vertical-align: -webkit-baseline-middle;
text-shadow:0 1px 1px rgba(25,25,25,0.52);
-webkit-font-smoothing:antialiased;
-webkit-transition: color 280ms linear, background 380ms ease-in, box-shadow 380ms ease-out, background-image 480ms linear;
transition:color 200ms, background-color 280ms linear, background 380ms ease-in, box-shadow 380ms ease-out, background-image 480ms linear, text-shadow 250ms;
}

body {
  color: rgba(147, 179, 179, 1.0);
  background: rgba(0, 0, 0, 0.0)!important;
  background-color: rgba(0, 0, 0, 0.0)!important;
  -webkit-font-smoothing:antialiased!important;
}


* {  
  color: lightseagreen;
  vertical-align: baseline; 
  background-color: rgba(0, 0, 0, 0.0);
  background-image: -webkit-linear-gradient(35deg, rgba(147, 179, 179, 0.15) 0%, rgba(0, 0, 0, 0) 50%), -webkit-linear-gradient(-35deg, rgba(147, 179, 179, 0.25) 0%, rgba(0, 0, 0, 0) 50%);
  box-shadow:0 3px 4px rgba(147, 179, 179, 0.4),inset 0 2px 3px rgba(47, 79, 79, 0.5);
  border-color: rgba(47, 79, 79, 0.5)!important;  
  -webkit-font-smoothing:antialiased;
}

div { 
  color: rgba(217, 231, 243, 0.85)!important;
  text-shadow:0 -1px 0 rgba(5,5,5,0.5);
  vertical-align: baseline; 
  background-color: rgba(47, 79, 79, 0.25)!important;
  background-image: -webkit-linear-gradient(145deg, rgba(147, 179, 179, 0.25) 25%, rgba(47, 79, 79, 0.35) 50%), -webkit-linear-gradient(-145deg, rgba(47, 79, 79, 0.35) 25%, rgba(147, 179, 179, 0.25) 50%);
  box-shadow:0 3px 4px rgba(47, 79, 79, 0.45),inset 0 2px 3px rgba(147, 179, 179, 0.35);
}

center, p, span, td {
  color: rgba(224, 255, 255, 0.9)!important;
  text-decoration: none;
  background-color: rgba(25, 25, 25,0.25)!important;
  background-size: 100% 1px;
  background: -webkit-radial-gradient(center,ellipse cover,rgba(0,0,0,0.05) 0%,rgba(0,0,0, 0.25) 45%,rgba(0,0,0,0.45) 90%);
  background-size: 1px 100%;
}


h1, h2, h3, h4, h5, h6 {
  color:  lightslategray;
  background: -webkit-radial-gradient(center,ellipse cover,rgba(0, 235, 255, 0.5) 0%,rgba(0, 0, 0, 0.75) 100%);
  background-color: darkturquoise;
  background-image: -webkit-radial-gradient(center center,ellipse cover,rgba(0, 235, 255, 0.5) 15%,rgba(0, 0, 0, 0.5) 100%);
  background-size: 100% 1px;
  font-weight: bold;
}

ul, ol, li {
  background-size: 100% 1px; 
  background:-webkit-radial-gradient(center center,ellipse cover,rgba(0, 0, 0, 0) 5%,rgba(0, 0, 0, 0.25) 45%,rgba(0, 0, 0, 0.5) 90%); 
  vertical-align: baseline; 
}

table, tbody, tfoot, thead, tr, th, td {
  background:-webkit-radial-gradient(center,ellipse cover,rgba(0,0,0,0.0) 5%,rgba(0,0,0,0.5) 45%,rgba(0,0,0,0.75) 90%); 
  vertical-align: baseline; 
  background-size: 100% 1px; 
}

abbr, acronym, address, big, cite, del, dfn, font, ins, kbd, q, s, samp, small, strike, sup, tt, var, b, u, i, dl, dt, dd, label {
  color:lightseagreen!important;
  background: -webkit-radial-gradient(center center,ellipse cover,rgb(255, 255, 255) 0%,rgb(0, 0, 0) 75%);
  background:-webkit-radial-gradient(center center,ellipse cover,rgba(128, 128, 128, 0.5) 0%,rgba(0, 0, 0, 0.75) 65%);
  background-size: 100% 1px; 
  vertical-align: baseline; 
}

/* HTML5 display-role reset for older browsers */


header, hgroup, menu, nav, button {
  background-size: 1px 100%;
  background-color: darkturquoise;
  background-image: -webkit-radial-gradient(center center,ellipse cover,rgba(0, 255, 255, 0.5) 1%,rgba(0, 0, 0, 0.8) 89%);
}

article, aside, details, figcaption, aside, figure, footer, section {
  background:-webkit-radial-gradient(center,ellipse cover,rgba(0,0,0,0.0) 0%,rgba(0,0,0,0.25) 44%,rgba(0,0,0,0.5) 96%); 
  vertical-align: baseline; 
  background-size: 100% 1px;
}

legend, caption, fieldset, form, applet, object, iframe, blockquote, pre, code, canvas, details, embed, figcaption, output, ruby, summary, time, mark, audio, video {
  background:-webkit-radial-gradient(center,ellipse cover,rgba(0, 0, 0, 0.05) 0%,rgba(0, 0, 0, 0.25) 45%,rgba(0, 0, 0, 0.45) 90%); 
  background-size: 100% 1px;
  vertical-align: baseline; 
  color: lightseagreen;
}

/*still need 'cellspacing="0"' in the markup */

img {
border-collapse: collapse;
border-spacing: 0;
padding: 1px 1px 2px;
box-shadow: 3px 4px 10px -2px rgba(28, 28, 28, 0.85);
background: -webkit-radial-gradient(center,ellipse cover,rgb(0, 0, 0) 47%,rgb(255, 245, 235) 93%);
}

input[type="button"], input[type="submit"], input[type="reset"]  {
-webkit-appearance: push-button;
white-space: pre;
background-color: rgba(107, 246, 13, 0.5)!important;
color: rgb(0, 241, 126)!important;
-webkit-rtl-ordering: logical;
-webkit-user-select: text;
cursor: auto;
box-sizing: border-box;
}
  
input, textarea, keygen, select, button, isindex {
white-space: pre;
-webkit-appearance: textfield;
background: rgba(255, 255, 255,0.65);
background-color: rgba(255, 255, 255,0.65)!important;
color: rgb(29, 32, 28)!important;
-webkit-rtl-ordering: logical;
-webkit-user-select: text;
cursor: auto;
box-sizing: border-box;
}
  
a:focus, a:active {
color: rgb(59, 89, 152);
text-decoration: underline;
}
    
a:link {
 background-color: darkturquoise;
 background-image: -webkit-radial-gradient(center top,ellipse cover,rgba(0, 0, 0,0.0) 5%,rgba(0, 0, 0, 0.25) 45%,rgba(0, 0, 0, 0.5) 96%);
  background-size: 100%;
  color: rgba(224, 234, 224,0.85);
  text-decoration:none;
  text-shadow:0 1px 1px rgba(15,15,15,0.5);
}

a:visited {
  background-color: rgb(0, 91, 240);
  background-image: -webkit-radial-gradient(center bottom,ellipse cover,rgba(0, 0, 0, 0) 0%,rgba(0, 0, 0, 0.25) 35%,rgba(0, 0, 0, 0.5) 100%);
  color:rgba(235,245,225,0.8);
  background-size: 100% 1px;
  text-decoration:none;
  text-shadow:0 1px 1px rgba(25,25,25,0.5);
  -o-transition:color 200ms,background 400ms,box-shadow 250ms;
  -moz-transition:color 200ms,background 400ms,box-shadow 250ms;
  -webkit-transition:color 200ms,background 400ms,box-shadow 250ms;
  transition:color 200ms,background 400ms,box-shadow 250ms;
}

a:hover{
  color:rgba(255,255,255,1);
  text-shadow:0 1px 1px rgba(55,55,55,0.5);
  -o-transition:color 200ms,background 400ms,box-shadow 250ms;
  -moz-transition:color 200ms,background 400ms,box-shadow 250ms;
  -webkit-transition:color 200ms,background 400ms,box-shadow 250ms;
  transition:color 200ms,background 400ms,box-shadow 250ms;
}
a > img {
border-collapse: separate;
border-spacing: 0;
padding: 1px 1px;
box-shadow: 0 0 15px 1px rgba(255, 255, 255, 0.5);
background: -webkit-radial-gradient(center,ellipse cover,rgb(238, 248, 255) 68%,rgb(0, 0, 0) 98%);
}

b, strong {
  font-weight: bold;
  background:-webkit-linear-gradient(45deg, rgba(147, 179, 179, 0.45) 45%, rgba(0, 0, 0, 0) 45%), -webkit-linear-gradient(-135deg, rgba(147, 179, 179, 0.55) 35%, rgba(0, 0, 0, 0) 35%), -webkit-linear-gradient(-45deg, rgba(147, 179, 179, 0.65) 35%, rgba(0, 0, 0, 0) 35%), -webkit-linear-gradient(225deg, rgba(147, 179, 179, 0.45) 45%, rgba(0, 0, 0, 0) 45%);
  background-size: 100% 1px;
}

em, i {
  background:-webkit-linear-gradient(45deg, rgba(147, 179, 179, 0.45) 45%, rgba(0, 0, 0, 0) 45%), -webkit-linear-gradient(-135deg, rgba(147, 179, 179, 0.55) 35%, rgba(0, 0, 0, 0) 35%), -webkit-linear-gradient(-45deg, rgba(147, 179, 179, 0.65) 35%, rgba(0, 0, 0, 0) 35%), -webkit-linear-gradient(225deg, rgba(147, 179, 179, 0.45) 45%, rgba(0, 0, 0, 0) 45%);
  background-size: 100% 1px;  
  font-style: italic;
}

ol, ul {
  list-style: none;
}

blockquote, q {
  quotes: none;
}

blockquote:before, blockquote:after,
q:before, q:after {
  content: '';
  content: none;
}

/* remember to define focus styles! */


:focus {
  outline: 0;
}

/* remember to highlight inserts somehow! */

ins, strong, em {
  text-decoration: none;
}

del {
  text-decoration: line-through;
}
*:not(:empty):not([onclick]):not(SPAN) {
color: rgba(0, 130, 130, 0.75)!important;
-webkit-background-clip: border-box;
-webkit-background-origin: padding-box;
-webkit-background-size: auto;
-webkit-box-shadow: rgba(255, 255, 255, 0.298039) 1px 3px 6px 9px inset;
-webkit-font-smoothing: antialiased;
-webkit-transition-delay: 0s, 0.1s, 0.2s, 0.3s, 0.4s, 0.5s;
-webkit-transition-duration: 0.25s, 0.358s, 0.458s, 0.55s, 0.658s, 0.825s;
-webkit-transition-property: color, background-color, background, box-shadow, background-image, text-shadow;
-webkit-transition-timing-function: ease, linear, ease-in, ease-out, linear, ease;
background-attachment: scroll;
background-clip: border-box;
background-color: rgba(0, 0, 0, 0.25)!important;
background: -webkit-radial-gradient(0% 50%, ellipse cover, rgba(0, 255, 225, 0.498039) 0%, rgba(0, 128, 98, 0.3498039) 45%, rgba(0, 64, 28, 0.2498039) 89%)!important;
background-image: -webkit-radial-gradient(45% 65%, ellipse cover, rgba(0, 255, 225, 0.498039) 0%, rgba(0, 128, 98, 0.3498039) 50%, rgba(0, 64, 28, 0.2498039) 100%)!important;
background-origin: padding-box;
background-size: auto 100%;
border-bottom-color: rgba(255, 255, 255, 0.5)!important;
border-bottom-style: solid;
border-bottom-width: 1px;
border-image-outset: 5px;
border-image-repeat: stretch;
border-image-slice: 100%;
border-image-source: none;
border-image-width: 0;
border-left-color: rgba(0, 0, 0, 0.5)!important;
border-left-style: solid;
border-left-width: 1px;
border-right-color: rgba(0, 0, 0, 0.25)!important;
border-right-style: solid;
border-right-width: 1px;
border-top-color: rgba(255, 255, 255, 0.25)!important;
border-top-style: solid;
border-top-width: 1px;
box-shadow: rgba(0, 28, 14, 0.298039) 2px 4px 6px 8px inset!important;
color: rgba(8, 248, 196, 0.75)!important;
cursor: auto!important;
direction: ltr;
font-family: 'lucida grande', tahoma, verdana, arial, sans-serif;
font-style: normal;
font-variant: normal;
font-weight: normal;
line-height: normal;
margin-bottom: 0;
margin-left: 0;
margin-right: 0;
margin-top: 0;
opacity: 0.9900000095367432;
outline-color: rgba(0, 0, 0, 0.25)!important;
outline-style: outset;
outline-width: thick;
padding-bottom: 0;
padding-left: 0;
padding-right: 0;
padding-top: 0;
text-shadow: rgba(255, 255, 255, 0.2521569) 1px -1px 0px;
transition-delay: 0s, 0s, 0s, 0s, 0s, 0s;
transition-duration: 0.2s, 0.28s, 0.38s, 0.38s, 0.48s, 0.25s;
transition-property: color, background-color, background, box-shadow, background-image, text-shadow;
transition-timing-function: ease, linear, ease-in, ease-out, linear, ease;
vertical-align: auto!important;
visibility: visible;
}
