css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("azlyrics.com") {
  body, .footer-wrap {
    background-color: rgb(18, 18, 18);
  }
  .main-page {
    color: rgb(95, 111, 158);
    font-family: ubuntu mono!important;
  }
  .lyricsh, h1 {
    text-transform: lowercase;
  }
  .lboard-wrap, .links-menu-wrap {
    display:none;
  }
  
  /* Top menu */
  
  .navbar-default {
    background-color: rgb(36, 42, 59);
    border-color: rgb(36,42,59);
  }
  
  .btn-menu, .navbar-footer, .btn-share {
    color: #ECA0A0;
    background-color: rgb(36,42,59);
    border-color: rgb(36,42,59);
    font-family: ubuntu mono;
    font-weight: 400 !important;
    text-transform: lowercase;
  }
  .navbar-brand > img {
    filter: invert(100%) hue-rotate(-99deg);
  }
  a {
    color:#ECA0A0;
    transition: all .2s ease;
    margin: -.2em;
    padding: .2em;
    border-radius: 3px;
    text-transform: lowercase;
  }
  a.focus, a:hover {
    color: #F98B8E;
    text-decoration: none;
    cursor: click;
    background-color: rgba(100, 199, 100, 0.32);
  }
  .btn-menu.focus, .btn-menu:focus, .btn-menu:hover, .btn-share.focus, .btn-share:focus, .btn-share:hover {
    color: #F98B8E;
    text-decoration: none;
    cursor: click;
    background-color: rgba(0, 0, 0, 0.32);
  }
  .input-group > .form-control {
    background-color: #0F0606;
    border: 0px;
  }
  .btn-primary,  .navbar-toggle.collapsed {
    color: #ECA0A0;
    background-color: #351414;
    border-color: #351414;
  }
  .form-control:focus {
    box-shadow: 0px 1px 1px rgba(0, 0, 0, 0.075) inset, 0px 0px 8px rgba(252, 160, 0, 0.6);
  }
  
  .btn-primary.active, .btn-primary.focus, .btn-primary:active, .btn-primary:focus, .btn-primary:hover, .open > .dropdown-toggle.btn-primary {
    color: #ECA0A0;
    background-color: #6E2A2A;
    border-color: #6E2A2A;
  }
  
  .breadcrumb, .panel {
    background-color: #2D1515;
  }
  
  .table > tbody > tr > td, .table > tbody > tr > th, .table > tfoot > tr > td, .table > tfoot > tr > th, .table > thead > tr > td, .table > thead > tr > th {
    border-top: 1px solid #272727;
  }
  
  .navbar-default .navbar-nav > li > a {
    color: #8A8A8A;
  }
}
