/* ALTERNATE ROWS */

.alternateRow { margin-top: 2px; padding: 1px 6px 0; line-height: 1.6em; }
.alternateRow { border-width: 0px !important; }
a.alternateRow { display: block; }

.alternate td,
.alternate th { position: relative; border: 0 solid transparent; border-width: 1px 0; }

.alternateRowHeader,
.alternate th {
  border: 0 !important; 
  
  background-color: #141 !important;
  background-image: -webkit-gradient(linear, #141 28%, #141) !important;
  background-image: -webkit-linear-gradient(#141 28%, #141) !important;
  background-image: -moz-linear-gradient(#141 28%, #141) !important;
  background-image: -ms-linear-gradient(#141 28%, #141) !important;
  background-image: -o-linear-gradient(#141 28%, #141) !important;
  background-image: linear-gradient(#141 28%, #141) !important;
}

/* normal */
.alternate .odd,
.alternate .odd td,
.alternateRow.alternateOdd,
.alternateOdd td { 
  background-color: #060 !important;
  border: 0 !important;
}  


.alternate .even,
.alternate .even td,
.alternateRow.alternateEven,
.alternateEven td { 
  background-color: #005a00 !important; 
  border: 0 !important;
}

/* :hover */
.alternateRow:hover,
tr.alternateRow:hover td { 
  background-color: #108010 !important; 
  border: 0px !important;
}
.alternateRow:hover .separatorB,
tr.alternateRow:hover .separatorB {
  background-color: transparent;
  border-color: transparent;
}


/* :marked */
.alternate .marked,
.alternate .marked td, 
.alternate .odd.marked td,

.alternateRow.alternateHighlight,
.alternateRow.alternateMarked,
.alternateHighlight td,
.alternateMarked td, 
.alternateOddMarked td,
.odd.alternateMarked td { 
  background-color: #218444 !important;
  border: 0 !important;
}

.alternate .even.marked,
.alternate .even.marked td,

.alternateRow.alternateEvenMarked,
.alternateEvenMarked td,
.even.alternateMarked td{ 
  background-color: #0d7a44 !important;
  border: 0 !important;
}

/* :marked:hover */
.alternate .marked:hover, 
.alternate .odd.marked:hover,
.alternate .even.marked:hover,
.alternate .marked:hover td, 
.alternate .odd.marked:hover td,
.alternate .even.marked:hover td,

.alternateHighlight:hover,
.alternateMarked:hover, 
.alternateOddMarked:hover,
.alternateEvenMarked:hover,
tr.alternateHighlight:hover td,
tr.alternateMarked:hover td, 
tr.alternateOddMarked:hover td,
tr.alternateEvenMarked:hover td {
  background-color: #11772a !important;
  border: 0 !important;
}
.body > .odd,
.odd > td { background-color: #060 !important;}

.body > .even,
.even > td { background-color: #005a00 !important; }

/* colored rows */
.body > .row:hover,
.row:hover > td,
.body > .marked,
.marked > td,
.body > .uiToggleOpen,
.uiToggleOpen > td { background-color: #108010 !important; }

.body .row-blue,
