css:
@-moz-document regexp("(http|https)://www.tumblr.*") {
#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content { width: 880px; }

body, div.l-content, .search_form_row, div.l-header .selection_nipple {
  background: #000;
  border-color: #000;
}

.chrome.blue {
  border: none;
  background: #989898;
}
.post_full .post_permalink:before {
  border-right-color: #000;
}
  
div.l-content, .search_form_row {
  border-radius: 5px;
}


.right_column:after, .controls_section.user_list li a:before, .controls_section.user_list li:hover a:before, .control_hover .controls_section.user_list li:active a:before {
  background: none;
  border: none;
}

/* nonsense below */
.right_column { float: left; }
.left_column, #left_column.followers {
  float: right;
  margin-left: 38px;
}

div.ui_follow, .controls_section li div.follow {
  color: #000;
  background: #989898;
}

.plexi.show.color {
  background: rgba(0,0,0,0.7);
}

}
