css:
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

.tab-close-button, 
.tabs-closebutton, 
#find-closebutton {
  list-style-image: url("data:image/png;base64,") !important;
  -moz-image-region: rect(0, 16px, 16px, 0) !important;
}

tab[selected="true"] .tab-close-button, 
.tabs-closebutton, 
tab:hover .tab-close-button, 
#find-closebutton {
  -moz-image-region: rect(16px, 16px, 32px, 0) !important;
}

tab .tab-close-button:hover, 
.tabs-closebutton:hover, 
#find-closebutton:hover {
  -moz-image-region: rect(32px, 16px, 48px, 0) !important;
}
