css:
	
@-moz-document domain("narutoforums.com") {
     
/** TOP OF THE PAGE */
    /** Changes the big Naruto Forums logo. */ 
    img[src="http://www.narutoforums.com/images/logo.gif"] {
        width: 0;
        height: 0;
        padding: 200px 0 0 800px;
        background: url("http://imgur.com/uPT3gkL.jpg");
        }
    
    /** Removes the "Your Ad Here Links - Stolen From Golden Circle */    
    div#pagenav_menu.vbmenu_popup + div { 
        display: none !important;
        }
   
    /** Changes the background color of that bar with "User CP, Blogs, FAQ, etc". */
    .vbmenu_control {
        background: none repeat scroll 0 0 #772527 !important;
        color: #FFFFFF !important;
        font: bold 11px tahoma,verdana,geneva,lucida,'lucida grande',arial,helvetica,sans-serif !important;
        padding: 3px 6px !important;
        white-space: nowrap !important;
        }
        
    /** Removes the barely visible dotted line border that's around every element on the page. */
    * {
        border: none !important;
        }    

    /** Changes the background of the box that says "Naruto Forums". It's located under the logo, above the User CP bar,
    and to the left of the box that contains the username.*/
/**    .alt1:nth-child(1) {
        background-image: none !important;
        background-color: #f6f0f0 !important;
        }
*/    
    /** Changes the background of the box that says "Welcome, Username." It's located under the logo,
    above the User CP bar, and to the right of the box that says "Naruto Forums". ADDTIONALLY, this changes */
/**    .alt2:nth-child(2) {
        background-image: none !important;
        background-color: #e4d6d6 !important;
        }
*/
/** Tables and Fonts */
    .time {
        color: #000000 !important;
        }

    .alt1, .alt1Active {
        background: none repeat scroll 0 0 #F6F0F0 !important;
        color: #000000 !important;
        }

    .alt1 a:link, .alt1_alink, .alt1Active a:link, .alt1Active_alink {
        color: #000000 !important;
        }
        
    .alt1 a:visited, .alt1_avisited, .alt1Active a:visited, .alt1Active_avisited {
        color: #000000 !important;
        }
        
    .alt1 a:hover, .alt1 a:active, .alt1_ahover, .alt1Active a:hover, .alt1Active a:active, .alt1Active_ahover {
        color: #772527 !important;
        }
        
    .alt2, .alt2Active {
        background: none repeat scroll 0 0 #E4D6D6 !important;
        color: #000000 !important;
        }
        
    .alt2 a:link, .alt2_alink, .alt2Active a:link, .alt2Active_alink {
        color: #772527 !important;
        }
        
    .alt2 a:visited, .alt2_avisited, .alt2Active a:visited, .alt2Active_avisited {
        color: #772527 !important;
        }
        
    .alt2 a:hover, .alt2 a:active, .alt2_ahover, .alt2Active a:hover, .alt2Active a:active, .alt2Active_ahover {
        color: #000000 !important;
        }
        
    .thead {
        background: none repeat scroll 0 0 #CFC1C0 !important;
        color: #000000 !important;
        font: 11px tahoma,verdana,geneva,lucida,'lucida grande',arial,helvetica,sans-serif !important;
        }

    .thead a:link, .thead_alink {
        color: #000000 !important;
        }

    .thead a:visited, .thead_avisited {
        color: #000000 !important;
        }

    .thead a:hover, .thead a:active, .thead_ahover {
        color: #000000 !important;
        text-decoration: underline !important;
        }

    .tfoot {
        background: url("http://i230.photobucket.com/albums/ee172/spy_smasher/Other/magenta_square.gif") repeat-x scroll left top #772527 !important;
        color: #FFFFFF !important;
        }

    .tfoot a:link, .tfoot_alink {
        color: #FFFFFF !important;
        }

    .tfoot a:visited, .tfoot_avisited {
        color: #FFFFFF !important;
    }

    .tfoot a:hover, .tfoot a:active, .tfoot_ahover {
        color: #FFFFFF !important;
        text-decoration: underline !important;
        }

    .tcat {
        background: url("http://i230.photobucket.com/albums/ee172/spy_smasher/Other/magenta_square.gif") repeat-x scroll left top #772527 !important;
        color: #FFFFFF !important;
        font: bold 10pt verdana,geneva,lucida,'lucida grande',arial,helvetica,sans-serif !important;
        }

    .tcat a:link, .tcat_alink {
        color: #FFFFFF !important;
        text-decoration: none !important;
        }

    .tcat a:visited, .tcat_avisited {
        color: #FFFFFF !important;
        text-decoration: none !important;
        }

    .tcat a:hover, .tcat a:active, .tcat_ahover {
        color: #FFFFFF !important;
        text-decoration: underline !important;
        }


/** ICONS AND IMAGES (excluding the logo) */

