css:
@-moz-document domain("narutoforums.com") {

/** Removes the "Your Ad Here Links - Stolen From Golden Circle */    
div#pagenav_menu.vbmenu_popup + div { 
    display: none !important;
}
body {
    background: none repeat scroll 0 0 #FFFFFF !important;
    color: #2A2A2A !important;
    font: 10pt verdana,geneva,lucida,'lucida grande',arial,helvetica,sans-serif !important;
    margin: 5px 10px 10px !important;
    padding: 0 !important;
}
a:link, body_alink {
    color: #2A2A2A !important;
}
a:visited, body_avisited {
    color: #AF3B3E !important;
}
a:hover, a:active, body_ahover {
    color: #AF3B3E !important;
}
.page {
    background: none repeat scroll 0 0 #FFFFFF !important;
    color: #2A2A2A !important;
    min-width: 1020px;
    max-width: 100%;
    min-height: 20%;
    max-height: 80%;
    resize: both;
    width: 1020px;
    margin-left: auto;
    margin-right: auto;
}
td, th, p, li {
    font: 10pt verdana,geneva,lucida,'lucida grande',arial,helvetica,sans-serif !important;
}
.tborder {
    background: none repeat scroll 0 0 #F7F7F7 !important;
    border: 1px dotted #000000 !important;
    color: #2A2A2A !important;
}
.tcat {
    background: none repeat scroll 0 0 #908D85 !important;
    color: #FFFFFF !important;
    font: bold 10pt verdana,geneva,lucida,'lucida grande',arial,helvetica,sans-serif !important;
}
.tcat a:link, .tcat_alink {
    color: #1F1E1C !important;
    text-decoration: none !important;
}
.tcat a:visited, .tcat_avisited {
    color: #1F1E1C !important;
    text-decoration: none !important;
}
.tcat a:hover, .tcat a:active, .tcat_ahover {
    color: #AF3B3E !important;
    text-decoration: underline !important;
}
.thead {
    background: none repeat scroll 0 0 #2A2A2A !important;
    color: #FFFFFF !important;
    font: bold 11px tahoma,verdana,geneva,lucida,'lucida grande',arial,helvetica,sans-serif !important;
}
.thead a:link, .thead_alink {
    color: #FFFFFF !important;
}
.thead a:visited, .thead_avisited {
    color: #FFFFFF !important;
}
.thead a:hover, .thead a:active, .thead_ahover {
    color: #AF3B3E !important;
}
.tfoot {
    background: none repeat scroll 0 0 #212121 !important;
    color: #E0E0F6 !important;
}
.tfoot a:link, .tfoot_alink {
    color: #E0E0F6 !important;
}
.tfoot a:visited, .tfoot_avisited {
    color: #E0E0F6 !important;
}
.tfoot a:hover, .tfoot a:active, .tfoot_ahover {
    color: #AF3B3E !important;
}
.alt1, .alt1Active {
    background: none repeat scroll 0 0 #FFFFFF !important;
    color: #2A2A2A !important;
}
.alt2, .alt2Active {
    background: none repeat scroll 0 0 #F5F5F5 !important;
    color: #2A2A2A !important;
}
.inlinemod {
    background: none repeat scroll 0 0 #FFFFFF !important;
    color: #000000 !important;
}
.wysiwyg {
    background: none repeat scroll 0 0 #FFFFFF !important;
    color: #2A2A2A !important;
    font: 10pt verdana,geneva,lucida,'lucida grande',arial,helvetica,sans-serif !important;
    margin: 5px 10px 10px !important;
    padding: 0 !important;
}
.wysiwyg a:link, .wysiwyg_alink {
    color: #2A2A2A !important;
}
.wysiwyg a:visited, .wysiwyg_avisited {
    color: #AF3B3E !important;
}
.wysiwyg a:hover, .wysiwyg a:active, .wysiwyg_ahover {
    color: #AF3B3E !important;
}
textarea, .bginput {
    font: 10pt verdana,geneva,lucida,'lucida grande',arial,helvetica,sans-serif !important;
}
.bginput option, .bginput optgroup {
    font-family: verdana,geneva,lucida,'lucida grande',arial,helvetica,sans-serif !important;
    font-size: 10pt !important;
}
.button {
    font: 11px verdana,geneva,lucida,'lucida grande',arial,helvetica,sans-serif !important;
}
select {
    font: 11px verdana,geneva,lucida,'lucida grande',arial,helvetica,sans-serif !important;
}
option, optgroup {
    font-family: verdana,geneva,lucida,'lucida grande',arial,helvetica,sans-serif !important;
    font-size: 11px !important;
}
.smallfont {
    font: 11px verdana,geneva,lucida,'lucida grande',arial,helvetica,sans-serif !important;
}
.time {
    color: #AF3B3E !important;
}
.navbar {
    font: 11px verdana,geneva,lucida,'lucida grande',arial,helvetica,sans-serif !important;
}
.highlight {
    color: #FF0000 !important;
    font-weight: bold !important;
}
.fjsel {
    background: none repeat scroll 0 0 #FFFFFF !important;
    color: #2A2A2A !important;
}
.fjdpth0 {
    background: none repeat scroll 0 0 #F7F7F7 !important;
    color: #2A2A2A !important;
}
.panel {
    background: none repeat scroll 0 0 #FFFFFF !important;
    border: 2px outset !important;
    color: #000000 !important;
    padding: 10px !important;
}
.panelsurround {
    background: none repeat scroll 0 0 #FFFFFF !important;
    color: #000000 !important;
}
legend {
    color: #000000 !important;
    font: 11px tahoma,verdana,geneva,lucida,'lucida grande',arial,helvetica,sans-serif !important;
}
.vbmenu_control {
    background: none repeat scroll 0 0 #AF3B3E !important;
    color: #FFFFFF !important;
    font: bold 11px tahoma,verdana,geneva,lucida,'lucida grande',arial,helvetica,sans-serif !important;
    padding: 3px 6px !important;
    white-space: nowrap !important;
}
.vbmenu_control a:link, .vbmenu_control_alink {
    color: #FFFFFF !important;
    text-decoration: none !important;
}
.vbmenu_control a:visited, .vbmenu_control_avisited {
    color: #FFFFFF !important;
    text-decoration: none !important;
}
.vbmenu_control a:hover, .vbmenu_control a:active, .vbmenu_control_ahover {
    color: #FFFFFF !important;
    text-decoration: underline !important;
}
.vbmenu_popup {
    background: none repeat scroll 0 0 #FFFFFF !important;
    border: 1px solid #0B198C !important;
    color: #000000 !important;
}
.vbmenu_option {
    background: none repeat scroll 0 0 #FFFFFF !important;
    color: #2A2A2A !important;
    cursor: pointer !important;
    font: 11px verdana,geneva,lucida,'lucida grande',arial,helvetica,sans-serif !important;
    white-space: nowrap !important;
}
.vbmenu_option a:link, .vbmenu_option_alink {
    color: #2A2A2A !important;
    text-decoration: none !important;
}
.vbmenu_option a:visited, .vbmenu_option_avisited {
    color: #AF3B3E !important;
    text-decoration: none !important;
}
.vbmenu_option a:hover, .vbmenu_option a:active, .vbmenu_option_ahover {
    color: #AF3B3E !important;
    text-decoration: none !important;
}
.vbmenu_hilite {
    background: none repeat scroll 0 0 #FFFFFF !important;
    color: #2A2A2A !important;
    cursor: pointer !important;
    font: 11px verdana,geneva,lucida,'lucida grande',arial,helvetica,sans-serif !important;
    white-space: nowrap !important;
}
.vbmenu_hilite a:link, .vbmenu_hilite_alink {
    color: #2A2A2A !important;
    text-decoration: none !important;
}
.vbmenu_hilite a:visited, .vbmenu_hilite_avisited {
    color: #AF3B3E !important;
    text-decoration: none !important;
}
.vbmenu_hilite a:hover, .vbmenu_hilite a:active, .vbmenu_hilite_ahover {
    color: #AF3B3E !important;
    text-decoration: none !important;
}
.bigusername {
    font-size: 14pt !important;
}
td.thead, th.thead, div.thead {
    padding: 4px !important;
}
.pagenav a {
    text-decoration: none !important;
}
.pagenav td {
    padding: 2px 4px !important;
}
.shade, a.shade:link, a.shade:visited {
    color: #777777 !important;
    text-decoration: none !important;
}
a.shade:active, a.shade:hover {
    color: #FF4400 !important;
    text-decoration: underline !important;
}
.tcat .shade, .thead .shade, .tfoot .shade {
    color: #DDDDDD !important;
}
.fieldset {
    margin-bottom: 6px !important;
}
.fieldset, .fieldset td, .fieldset p, .fieldset li {
    font-size: 11px !important;
}

