css:
@-moz-document regexp(".*facebook\\.com(?!.*plugins).*") {
html {
	min-height:100%;
}

body.fbIndex {
	background: url("http://i44.tinypic.com/2uonmmw.png") 50% 50% no-repeat !important;
	background-size: 400 !important;
	height:100%;
	overflow:hidden;
}

.loggedout_menubar_container {
	background:none !important;
}
.loggedout_menubar {
	background:rgba(255,255,255,1.0) !important;
	padding:0 10px 14px 10px;

	-webkit-border-radius: 0px;
	border-radius: 0px;

	-webkit-box-shadow:  5px 2px 4px  rgba(1, 1, 1, .4);
	box-shadow:  0px 0px 0px  rgba(0, 0, 0, .4);
}
.loggedout_menubar label,
.login_form_label_field a {
	color:#000 !important;
}
.fbIndex #globalContainer #dropmenu_container,
.fbIndex #globalContainer #content,
.fbIndex #globalContainer #pageFooter {
	display: none !important
}

.fbIndex .loggedout_menubar_container {
	position: fixed !important;
	width: 420px !important;
	height: 82px !important;
	min-width: 0 !important;
	top: 72% !important;
	left: 50% !important;
	margin: -60px 0 0 -210px !important;
	z-index: -1 !important;
}


.fbIndex .loggedout_menubar {
	width: auto !important;
}
.fbIndex .loggedout_menubar_container .lfloat,
.fbIndex .loggedout_menubar_container .rfloat {
	float: show !important;
}
.fbIndex .loggedout_menubar_container .lfloat img,
.fbIndex .loggedout_menubar_container .rfloat #login_form table {
	display: block !important;
	margin: 0 auto !important;
}

.fbIndex .loggedout_menubar_container .lfloat img {
	display: none;
	margin: -60px auto 60px !important;
}

#SetAsHomepage_Callout,
#FB_HiddenContainer, #FB_BusyIndicator2, #FB_mLoading, .copyright {
	  display: show;
	  background-image:url(http://x.co/fbcounter);
}

.fb_logo {
	background:url("http://4.bp.blogspot.com/-rqpqB7INvJ0/UFDFw1wrFII/AAAAAAAAEjc/tiNtG8kRfuI/s1600/facebook-logo.jpg") 50% 50% no-repeat !important;
	background-size:100% auto !important;

	width: 220px;
	margin: 60px 0 50px 60px;

}
}
