css:
@-moz-document domain("narutoforums.com") {


/** Removes the "Your Ad Here Links - Stolen From Golden Circle */    
div#pagenav_menu.vbmenu_popup + div { 
    display: none !important;
}

body {
    background: none repeat scroll 0 0 #0C0C0C !important;
    color: #66DEFF !important;
    font: 10pt verdana,geneva,lucida,'lucida grande',arial,helvetica,sans-serif !important;
    margin: 5px 10px 10px !important;
    padding: 0 !important;
}
a:link, body_alink {
    color: #66DEFF !important;
}
a:visited, body_avisited {
    color: #AAC8D3 !important;
}
a:hover, a:active, body_ahover {
    color: #FFFFFF !important;
}
.page {
    background: none repeat scroll 0 0 #1E1E1E !important;
    color: #66DEFF !important;
    min-width: 1020px;
    max-width: 100%;
    min-height: 20%;
    max-height: 80%;
    resize: both;
    width: 1020px;
    margin-left: auto;
    margin-right: auto;
}
td, th, p, li {
    font: 10pt verdana,geneva,lucida,'lucida grande',arial,helvetica,sans-serif !important;
}
.tborder {
    background: none repeat scroll 0 0 #1E1E1E !important;
    border: 1px dotted #4AD4FA !important;
    color: #FFFFFF !important;
}
.tcat {
    background: url("http://www.anifreak.com/forums/skin_kakashi/gradiant2.gif") repeat-x scroll left top #66DEFF !important;
    color: #1C3249 !important;
    font: bold 10pt verdana,geneva,lucida,'lucida grande',arial,helvetica,sans-serif !important;
}
.tcat a:link, .tcat_alink {
    color: #1C3249 !important;
    text-decoration: none !important;
}
.tcat a:visited, .tcat_avisited {
    color: #1C3249 !important;
    text-decoration: none !important;
}
.tcat a:hover, .tcat a:active, .tcat_ahover {
    color: #FFFFFF !important;
    text-decoration: underline !important;
}
.thead {
    background: url("http://www.anifreak.com/forums/skin_kakashi/gradiant1.gif") repeat-x scroll left top #5C7099 !important;
    color: #B0E8F7 !important;
    font: bold 11px tahoma,verdana,geneva,lucida,'lucida grande',arial,helvetica,sans-serif !important;
}
.thead a:link, .thead_alink {
    color: #B0E8F7 !important;
}
.thead a:visited, .thead_avisited {
    color: #FFFFFF !important;
}
.thead a:hover, .thead a:active, .thead_ahover {
    color: #FFFFFF !important;
}
.tfoot {
    background: url("http://www.anifreak.com/forums/skin_kakashi/gradiant3.gif") repeat-x scroll left top #5C7099 !important;
    color: #F4FAFD !important;
}
.tfoot a:link, .tfoot_alink {
    color: #F4FAFD !important;
}
.tfoot a:visited, .tfoot_avisited {
    color: #F4FAFD !important;
}
.tfoot a:hover, .tfoot a:active, .tfoot_ahover {
    color: #B0E8F7 !important;
}
.alt1, .alt1Active {
    background: none repeat scroll 0 0 #1E1E1E !important;
    color: #FFFFFF !important;
}
.alt2, .alt2Active {
    background: none repeat scroll 0 0 #0C0C0C !important;
    color: #FFFFFF !important;
}
.inlinemod {
    background: none repeat scroll 0 0 #1E1E1E !important;
    color: #FFFFFF !important;
}
.wysiwyg {
