    color: #FFFFFF !important;
    padding: 10px !important;
}
.panelsurround {
    background: none repeat scroll 0 0 #1E1E1E !important;
    color: #FFFFFF !important;
}
legend {
    color: #66DEFF !important;
    font: 11px tahoma,verdana,geneva,lucida,'lucida grande',arial,helvetica,sans-serif !important;
}
.vbmenu_control {
    background: url("http://www.anifreak.com/forums/skin_kakashi/gradiant1.gif") repeat-x scroll left top #5C7099 !important;
    color: #FFFFFF !important;
    font: bold 11px tahoma,verdana,geneva,lucida,'lucida grande',arial,helvetica,sans-serif !important;
    padding: 3px 6px !important;
    white-space: nowrap !important;
}
.vbmenu_control a:link, .vbmenu_control_alink {
    color: #FFFFFF !important;
    text-decoration: none !important;
}
.vbmenu_control a:visited, .vbmenu_control_avisited {
    color: #FFFFFF !important;
    text-decoration: none !important;
}
.vbmenu_control a:hover, .vbmenu_control a:active, .vbmenu_control_ahover {
    color: #FFFFFF !important;
    text-decoration: underline !important;
}
.vbmenu_popup {
    background: none repeat scroll 0 0 #FFFFFF !important;
    border: 1px solid #0B198C !important;
    color: #000000 !important;
}
.vbmenu_option {
    background: none repeat scroll 0 0 #1E1E1E !important;
    color: #FFFFFF !important;
    cursor: pointer !important;
    font: 11px verdana,geneva,lucida,'lucida grande',arial,helvetica,sans-serif !important;
    white-space: nowrap !important;
}
.vbmenu_option a:link, .vbmenu_option_alink {
    color: #FFFFFF !important;
    text-decoration: none !important;
}
.vbmenu_option a:visited, .vbmenu_option_avisited {
    color: #B0E8F7 !important;
    text-decoration: none !important;
}
.vbmenu_option a:hover, .vbmenu_option a:active, .vbmenu_option_ahover {
    color: #66DEFF !important;
    text-decoration: none !important;
}
.vbmenu_hilite {
    background: none repeat scroll 0 0 #66DEFF !important;
    color: #FFFFFF !important;
    cursor: pointer !important;
    font: 11px verdana,geneva,lucida,'lucida grande',arial,helvetica,sans-serif !important;
    white-space: nowrap !important;
}
.vbmenu_hilite a:link, .vbmenu_hilite_alink {
    color: #FFFFFF !important;
    text-decoration: none !important;
}
.vbmenu_hilite a:visited, .vbmenu_hilite_avisited {
    color: #FFFFFF !important;
    text-decoration: none !important;
}
.vbmenu_hilite a:hover, .vbmenu_hilite a:active, .vbmenu_hilite_ahover {
    color: #FFFFFF !important;
    text-decoration: none !important;
}
.bigusername {
    font-size: 14pt !important;
}
td.thead, th.thead, div.thead {
    padding: 4px !important;
}
.pagenav a {
    text-decoration: none !important;
}
.pagenav td {
    padding: 2px 4px !important;
}
.shade, a.shade:link, a.shade:visited {
    color: #777777 !important;
    text-decoration: none !important;
}
a.shade:active, a.shade:hover {
    color: #FF4400 !important;
    text-decoration: underline !important;
}
.tcat .shade, .thead .shade, .tfoot .shade {
    color: #DDDDDD !important;
}
.fieldset {
    margin-bottom: 6px !important;
}
.fieldset, .fieldset td, .fieldset p, .fieldset li {
    font-size: 11px !important;
}

