css:
	@-moz-document url("chrome://browser/content/browser.xul") {

	#downloadsbar-bar {
	}



	/* bar */
	#downloadbar-bar-wrbx {
	}
		/* outer box */
		.downloadbar-dwnldtmstck {
		margin: 0 !important; /* remove space b/w boxes */
		border: none !important; /* remove/replace gray border */
		}
			/* download boxes */
			#downloadbar-bar hbox stack hbox {
			box-shadow: inset 0 0 0px 1px rgba(0,0,0,0.2) !important;
			transition: background-size 500ms !important; /* makes progress smooth - messes with pause and download complete though */
			}
				/* entire label (image and text) */
				.downloadbar-dwnldtmhbx {
				font-family: Segoe UI !important;
				font-size: 11px !important;
				color: rgba(0,0,0,0.65) !important;
				}
					/* label (excluding app icon) */
					[id^=downloadbar-label] {

					}
					/* label - progress info */
					/* #downloadbar-bar hbox hbox label:nth-child(3)*/
					[id^=downloadbar-labelprogress] {
					font-size: 10px !important;
					
					}
					
	.downloadbar-dwnldtmstck[paused=true] {
	font-style: italic !important;
	}
					
					

	}
