css:
@-moz-document url(http://www.cnbeta.com/) {
/* By percy */
	.global_head,.global_head +section,.slide,.cbv,.tab_gray,.cb_box +section,.focus,.all_news_wildlist .newsinfo p + .clear,.main_content_left >section:nth-child(4),.white_bottom{display: none !important;}
	.copyright{display: none !important;}
	.headline{float: none !important; width: auto !important; padding-left: 0px !important;}
	.headline .top_text{width: auto !important; position: static !important; height: auto !important; }
	.headline .top_img{height: auto !important; position: absolute; left: -90px; top: -24px;}
	.headline dt{margin-bottom: 0px !important; }
	.headline dd{}
	.headline dl{height: auto !important; min-height: 78px; padding-left: 100px !important; }
	.headline_area{height: auto !important;}
	.cb_box{height: auto !important; padding: 5px !important;}
	.imgbox85x62{display: block !important; }
	.main_content_left{width: 100% !important;}
	.main_content_right{right: -240px;position:fixed!important;transition: all ease 1s;}
	.main_content_right:hover {right: -15px;position:fixed!important;}

	.main_content {overflow: visible!important;font-size:16px!important;}
	.blue_bar{background: #538AC1 !important;}
	.realtime{float: none !important; width: auto !important; padding-left: 0px !important; height:auto !important;}
	.realtime_list dl{padding: 0px !important;}
	.all_news_wildlist dl{width:  auto !important; margin:0 5px !important; padding: 5px 10px !important;  float: none !important; display: block !important; min-height: 110px; }
	.all_news_wildlist dl:hover{box-shadow: 0 0 5px rgba(0,0,0,0.2);}
	.all_news_wildlist dt{margin-bottom: 0px !important; margin-left: 195px !important; }
	.all_news_wildlist dd:before, .all_news_wildlist dd:after{content: " "; display: block;  height: 0; visibility: hidden;}
	.all_news_wildlist dd:after{clear: both;}
	.all_news_wildlist .newsinfo p{height: auto !important; max-height: 78px; overflow: hidden;}
	.all_news_wildlist .read{top: -28px !important;}
	.all_news_wildlist .tools{}
	.tplist .newsinfo, .all_news_wildlist .newsinfo{width: auto !important; position: relative !important; top: auto !important; left: 0px !important; margin-left: 195px !important; }
	.tplist .pic, .all_news_wildlist .pic{display: block !important; height: auto !important; float: left; margin-top: -30px !important; }
	.clear{height: 0px !important;}

	.wrapper{width: 1000px!important;margin-left: 150px!important;}

	.read_more .more{ background: #F5F5F5 !important;}
	.items_area{overflow: hidden;}
	
	
	.main_content +section, #googleAd_afc, .commt_r{display: none !important;}
dl dt a{font-size:18px!important;}
.headline .top_text {font-size:15px!important;}
	/* content center */
	.article .body .content{text-align: center;}
	.article .body .content img{display: block; margin: 0 auto !important;font-size:16px!important;}
.comments_side {height:655px!important;}
.comments_list {height:655px!important;}
.realtime_list dt {width: auto!important;}
}
