css:
@-moz-document url-prefix("http://www.cnbeta.com/articles"), url-prefix("http://cnbeta.com/articles") {

nav, .global_head,.rating_box,.article .navi,.side_news_list,.bdshare_b a.shareCount,footer.copyright,.cber,section.cb_box,.left_art{display: none !important;}
	.article .navi,.cbv,#cproIframe2,center,ins,.post_commt .commt_r,#BAIDU_DUP_wrapper_693405_0{display:none !important;}
	#googleAd_afc {display:none !important;}
	.main_content {width: 1200px;margin: 0 0 0 -80px!important;}


div#BAIDU_DUP_wrapper_685246_0{display:none!important;}
div.content_body{width: 1050px;}	
	
	.main_content_left {width: auto !important;}

.main_content_right {right: -292px;position:fixed!important;transition: all ease 1s;}
.main_content_right:hover {right: 5px;position:fixed!important;}


	article {width: 1050px !important;transition: all ease 1s;}
	
	.fr {position: fixed !important;left:4px !important;bottom: 4px !important;margin: 0 -560px 0 0 !important;width: 60px !important;}
	
	body {background: #494949 url('https://res.wx.qq.com/zh_CN/htmledition/images/bg_universal17ced3.png')!important;}
	#bdshare img{width: 100px !important;}
	.content_body{background: none !important;}
	.article .body .introduction {font-size: 18px!important;width: 660px!important;}
	.article .body .content {font-size: 18px!important;}
    .hotcomments ul{font-size: 15px!important;}
	.post_commt {background: rgba(64, 69, 74, 0.9);border-left: 0px solid #CCC !important;border-right: 0px solid #CCC !important;border-radius: 5px;border-top: 1px solid #444 !important;border-bottom: 1px solid #222 !important;box-shadow: 0 0 10px #000;z-index: 100;position: fixed !important;bottom: 10%;opacity: 1;padding: 0 24px;left: -390px;width:auto!important;transition: all ease 1s;}

	.post_commt .login .logoA, .post_commt .active, .post_commt .disabled {
	border-radius: 4px;
}

.post_commt:hover {
	left: -20px;
 
	opacity: 1;
}

.commt_list blockquote, .left_art_box a {
	background: rgba(72,72,72,0.3) !important;
	border: 0px solid #999 !important;
	border-radius: 5px;
	box-shadow: 0 0 1px rgba(0,0,0,1);
	border-top: 1px solid #666 !important;
	border-bottom: 1px solid #222 !important;
	color: #eee !important;
}
.commt_list dl {
	z-index: 100;
	padding: 1px;
	margin-bottom: 16px;
	background: rgba(0,0,0,0.3);
	border-left: 0px solid #ccc !important;
	border-right: 0px solid #ccc !important;
	border-radius: 5px;
	border-bottom: 1px solid #666 !important;
	border-top: 1px solid #222 !important;
}
.commt_list nav {
	background: none !important;
	border: 0px !important;
	color: #eee;
}
.commt_list {
	z-index: 100;
}
.commt_list img {
	border-radius: 5px;
}

.commt_list dt {
	padding: 5px 10px;
	background: none !important;
}
.commt_list .comment_body {
	padding-left: 60px;
}
.commt_list blockquote p.re_text, .commt_list .re_text {
	color: #ddd !important;
}
.commt_list .re_lou,.commt_a_link span {color: #DDD !important;}

.article .body .content {margin-left:auto!important;margin-right:auto!important;width:90%!important;}
.article .body .content p > embed {margin-left:150px!important;margin-right:auto!important;width:60%!important;}
iframe {margin-right:auto!important;width:90%!important;}
.article .body .content p {text-align: left!important;}
}
