css:
/*======================================================
  jp.indeed.com indeed求人情報 ja jp liquid compact 2014
  ======================================================*/
@-moz-document domain("jp.indeed.com") {
/*--- unwanteds ---*/


/*--- page containers ---*/
body {margin: 0 !important;}
body>table {width: 100% !important;}

/*--- navigation ---*/
#g_nav, #g_nav *
 {margin: 0 !important; padding: 0 !important;}

/*--- search bar ---*/
body>table td[width='1125'] {width: auto !important;}
body>table td[width='1125'] table td {width: auto !important; margin: 0 !important; padding: 0 !important;}
body>table td[width='1125'] a#branding img {width: auto !important; height: 2em !important; margin: 0 !important;}
#what, #where {width: 100% !important;}

/*--- topic ---*/
body>table td[style^='padding'] {margin: 0 !important; padding: 0 !important;}
#job_header {margin: 0 !important;}
#job_header br {display: none !important;}
#job_header+table {width: 100% !important;}
.snip .summary {width: 100% !important;}
.snip .summary br:nth-child(even) {display: none !important;}
.snip .summary ul {margin: 0 !important; padding: 0 !important;}

#bvjl {
 position: fixed !important; top:0; left: 50%;
 float: left;
 width: auto;
 background-color: white;
 opacity: 0.5;
 margin: 0 !important;
 text-align: center;
 }

.recJobs .sjr span+br {display: none !important;}

#share_this_job {margin: 0 !important;}
#share_this_job table {width: 100% !important;}

#relatedLinks {margin: 0 !important;}

}
