@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("mediawiki.org"),
domain("wikipedia.org"),
domain("wikimedia.org"),
domain("wikinews.org"),
domain("wikibooks.org"),
domain("wikiquote.org"),
domain("wikiversity.org"),
domain("wikisource.org"),
domain("wiktionary.org"),
domain("wikimediafoundation.org"),
domain("toolserver.org"),
domain("wikia.com"),
domain("wikihow.com"),
domain("wikileaks.org"),
domain("wikimapia.org")


 {

/*text&background*/

#column-content, #commentrequest, #content, #content ol, #content ul, #content_wrapper, #content_wrapper.show, #content-container, #firstHeading, #footer, #fragment-11, #MainPageBox1, #MainPageBox2, #mw_content ol, #mw_content ul, #mw_contentholder div, #mw_contentwrapper #mw_content, #mw_main #mw_contentwrapper, #mw-head, #mw-mf-page-center, #mw-normal-catlinks, #mw-protect-cascadeon, #otherlangs, #page-index, #p-Community, #searchform fieldset, #searchInput, #simpleSearch #searchInput, #toc, #toc #toctitle, #toc .toctitle, #toc h2, #WikiaArticle div div, #WikiaRail section, #wl-popup, .articleFeedback-panel .articleFeedback-buffer, .catlinks, .center *, .divTop10item, .drawer, .dropdown, .feedback_reviewform, .freenews, .header, .header_left_text, .l_table, .latest_news, .lead_normal, .main, .main_popular, .mp_header, .mw-tou_notice, .mw-warning, .noprint, .overlay-header, .overlay-header-container, .plainlinks, .portal, .portals, .the_table, .thumb, .thumbcaption, .toc, .toc #toctitle, .toc .toctitle, .toc h2, .toccolours, .welcome_to_wn, b, body, code, div.donate, div.donate2, div.lyricbox, div.mw-cascadeprotectedwarning, div.mw-content-ltr div, div.mw-lag-warn-high, div.mw-warning-with-logexcerpt, div.nav a, div.NavFrame div.NavHead, div.vectorTabs li.selected a, div.vectorTabs li.selected a:visited, div.vectorTabs span > a, div.WikiaHubBranding div.hub, div.WikiaPageContentWrapper, element.style, figure.thumb, form.WikiaSearch input, h1, h2, h3, h4, h5, h6, header.WikiaHeader nav, html, li, nav.WikiaArticleCategories, p, table, td, th, tr td div
{
background-color: black !important;
color: white !important;
}

.header
{
background: black !important;
}

td, th
{
border-color: white !important;
}

/*links*/

a
{
color: #00a0ff !important;
}
a.button
{
color: white !important;
}
#mw-mf-last-modified a
{
color: #00c0ff !important;
}

/*highlight*/

#simpleSearch, .infobox, .mw-geshi, .mw-mf-overlay, .top-bar, input, pre
{
background-color: #333333 !important;
color: white !important;
}
.top-bar.active
{
background-color: #009356 !important;
}
.search-overlay, .overlay
{
background: rgba(0,0,0,0.8) !important;
}

/*imagebackgroundreset*/

tbody tr th a img, #globalWrapper #bodyContent div img, div p a img, #mw-panel.noprint #p-logo, #panel.noprint #p-logo
{
background-color:white!important;
color:black!important;
}

/* ui-images */

h2.section_heading, a.close
{
background-image: url('data:image/png;base64,') !important;
}

h2.section_heading.openSection
{
background-image: url('data:image/png;base64,') !important;
}

#mw-mf-main-menu-button
{
background-image: url('data:image/png;base64,') !important;
}

button.cancel
{
background-image: url('data:image/png;base64,') !important;
}

}
