css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain('board.freedomainradio.com') {
 
    /*
        LAYOUT AND GLOBAL CHANGES
    */
    
    * {
        font-family: 'Helvetica Neue', Helvetica, Arial, sans-serif !important;
    }
 
    .wrapper {
        width: 90% !important;
    }
    
    /*
        BRANDING AND TOP NAV
    */
    
    #branding {
        height: 66px !important;
    }
 
    img[alt="Logo"] {
        height: 60px;
        margin-top: 5px;
    }
    
    #primary_nav {
        margin: 20px 0 0 0 !important;
    }
    
    #primary_nav a {
        box-shadow: none !important;
        font-weight: normal;
        text-transform: lowercase;
    }
    
    /*
        USER AND SEARCH BAR
    */
    
    #user_bar {
        margin: 0 0 5px 0 !important;
        height: 38px !important;
    }
    
    #user_navigation {
        height: 1.6em !important;
        margin: 0 !important;
        overflow: visible !important;
        padding: 1px 0 0 0 !important;
    }
    
    #user_navigation a {
        font-weight: normal !important;
        height: 27px !important;
    }
    
    .user_photo {
        height: 26px !important;
        margin-top: 2px;
    }
    
    #search_options_menucontent, #search_options_menucontent * {
        font-size: 11px !important;
        font-weight: normal !important;
    }
    
    #main_search {
        font-size: 11px !important;
    }
    
    /*
        BREADCRUMBS AND SUBNAV
    */

    #secondary_navigation {
        height: auto !important;
    }
    
    #secondary_navigation, #secondary_navigation * {
        font-size: 11px;
        font-weight: normal !important;
        line-height: normal !important;
        height: auto !important;
        margin: 0 !important;
        padding: 0 !important;
    }
    
    #secondary_navigation a {
        padding: 5px !important;
    }
    
    #breadcrumb a {
        /* having a background on this was glitching out the style */
        background-image: none !important;
        padding: 4px !important;
    }
    
    #breadcrumb a span {
        display: inline-block;
        padding: 2px 17px 2px 6px !important;
    }
    
    #breadcrumb li > span {
        padding: 6px !important;
    }
    
    #secondary_links a {
        padding-top: 6px !important;
    }
    
    #secondary_links a:hover {
        background-color: #BBCEE0;
    }
    
    #secondary_links img {
        margin-top: -3px !important;
    }
    
    /*
        CONTENT SECTION
    */
    
    #contentOut {
        padding: 4px !important;
    }
    
    .box {
        border: none !important;
        padding: 2px 4px 3px 4px !important;
    }
    
    .maintitle {
        padding: 7px !important;
    }
    
    .maintitle .toggle {
        visibility: visible !important;
    }
    
    .message {
        margin-bottom: 0 !important;
    }
    
    /*
        FORUM LIST
    */
    
    
    
    /*
        LEFT SIDE AND RIGHT SIDE BOXES
        + Notifications
        + Folders
    */
    
    .ipsSideBlock h3, .general_box h3 {
        padding: 3px 6px !important;
    }
    
    /*
        PROFILES
    */
    
    #profile_tabs a {
        padding: 6px 8px !important;
    }
    
    .ipsUserPhoto.ipsUserPhoto_mini {
        display: inline-block;
        overflow: hidden;
        width: 36px !important;
        height: 36px !important;
    }

}

@-moz-document url('http://board.freedomainradio.com/chat/') {
    /*
        CHAT
    */
    
    h3.bar {
        padding: 3px 5px !important;
    }
    
    #chatters-online .names {
        margin-left: 5px;
    }
    
    .ipsPostForm .ipsLayout_right {
        width: 190px !important;
    }
    
    .ipsPostForm_sidebar {
        margin-top: 0 !important;
    }

    .box_in .ipsBox.ipsLayout.ipsLayout_withright.ipsPostForm.clearfix {
        padding: 0 !important;
    }
    
    .ipsBox_container.ipsLayout_content {
        box-shadow: none !important;
        float: left !important;
        width: 80% !important;
        margin: 0 !important;
        padding: 0 !important;
        border: none !important;
    }

    .ipsBox_container.ipsLayout_right.ipsPostForm_sidebar {
        float: right !important;
        width: 20% !important;
        margin: 0 !important;
        padding: 0 !important;
        border: none !important;
    }
    
    /*
    .post .ipsUserPhoto.ipsUserPhoto_mini {
        width: 42px !important;
        height: 42px !important;
    }
    
    .chat-message > label {
        margin: 
    }
    
    .chat-message > div {
        
    }
    */
    
    .post {
        display: flex;
    }
    
    .post > img.ipsUserPhoto.ipsUserPhoto_mini, .post > label, .post > div {
        display: inline-block !important;
        margin: 2px 6px 2px 3px !important;
        float: none !important;
    }
    
    .post > label {
        width: 150px;
        text-align: right;
        order: 1;
    }
        
    .post > img.ipsUserPhoto.ipsUserPhoto_mini {
        order: 2;
    }
    
    .post > div {
        order: 3;
    }
    
    .post .hider {
        background: transparent !important;
        position: static;
    }
    
    .post .hider + div {
        margin-left: 210px !important;
    }
    
}
