css:
@namespace url(http://www.w3.org/1999/xhtml);
  
@-moz-document domain("www.kaskus.co.id"), domain("fjb.kaskus.co.id") {
  
  @-moz-keyframes apung {
	0% {margin-bottom: 0; opacity: .3;}
	50% {margin-bottom: 20px;  opacity: .9;}
	100% {margin-bottom: 0;  opacity: .3;}
  }
  @-moz-keyframes puang {
	0% {margin-bottom: 0; opacity: .9;}
	50% {margin-bottom: 20px;  opacity: .3;}
	100% {margin-bottom: 0;  opacity: .9;}
  }
  #feedback {
    top: 70%;
    right: -120px !important;
    left: auto;
    right: 0;
    display: inline-block;
    opacity: .8;
    width: 150px !important;
    transform: rotate(0deg) !important;
    animation: 5s ease-in-out 0s normal none infinite apung !important;
  }
  #feedback:hover {
    opacity: 1;
  }
  body:after {
    content: url("http://puu.sh/52TRi");
    right: 10px !important;
    bottom: 10px;
    position: fixed;
    display: inline-block;
    animation: 5s ease-in-out 0s normal none infinite apung !important;
}
  body:before {
    content: "Yahallo!";
    font-family: Curlz MT;
    font-weight: bold;
    color: #6AA90B;
    background: rgba(255, 255, 255, .8);
    border-radius: 1em 1em 0em 1em;
    box-shadow: 3px 6px 9px rgba(0, 0, 0, .5);
    display: none;
    position: fixed;
    padding: 10px 0px;
    bottom: 220px;
    right: 110px !important;
    width: 100px !important;
    text-align: center;
    text-shadow: none !important;
    z-index: 9 !Important;
    animation: 5s ease-in-out 0s normal none infinite puang !important;
  }
  body:hover:before {
    display: inline-block !important;
    font-size: 20px !important;
  }
  body:active:before {
    content: "Cip!Cip!Cip!";
    bottom: 220px;
    right: 110px !important;
  }
}
