css:
@-moz-document domain("nyaa.se") {

#searchcontainer select,
#searchcontainer input { 
    color: #cccaca;
    background-color: #333;
    border: none;
}
    
#searchcontainer input.inputsearchterm { padding: 3px; }
    
.chatbox { display: none; }

input,
select,
textarea,
.content,
#tabnav,
.inputsearchcategory,
option,
select,
input,
textarea,
#searchcontainer,
.inputsearchterm,
.inputsearchsubmit{
    font: 11px "Sazanami Gothic" !important; /* MAIN FONT */
   }

a:hover,
b,
div.comment .chead a span,
div.comment .chead span{
    color:#cccaca !important; /* HIGHLIGHT COLOR */
    }

body{
    background:#2d2d2d !important;
   }

.content{
    border-width: 0px !important;
    border-color: #333 !important;
    border-radius: 1px!important;
    background:#333 !important;
    color: #cccaca!important;
    margin-left: auto!important;
    margin-right: auto!important;
    margin-top:0px!important;
    padding-bottom: 0px!important;
    margin-bottom: 30px!important;
    float:none!important
   }

a{
   color: #cccaca!important;
   }

a:hover{
    text-decoration: none!important;
   }

#tabnav,
.inputsearchcategory,
option,
select,
input,
textarea,
#searchcontainer,
.inputsearchterm,
.inputsearchsubmit{
    color: rgba(255,255,255,0.8)!important;
   }

#main div a img{
   display:none;
   }

#main div.viewdownloadbutton a img{
   display:block;
   }

td.tlistdownload a img{
   display:inherit!important;
   }

.viewdescription, .viewfiletable{
    border-top:none!important;
    border-right:none!important;
    border-bottom:none!important;
    border-radius:1px!important;
    border-color:#333!important;
    background:#333!important;
    }

.tlist a, .tlist td{
   font-weight:normal !important;
   }

.refinesearch, .tlistththree, .torrentcats{
    display: none!important
   }

#topbar{
    top:0px!important;
    left:48px;
    height: 0px!important;
    position:absolute!important;
    }

#topbar:hover{
    top:0px!important;
    display: block!important;
   }

#searchcontainer{
    padding: 11px 0px 7px 0px!important
   }

.inputsearchterm{
    width: 136px!important;
    height: 9px!important;
   }

.tlistsortorder{
    margin-left:auto!important;
    margin-right:auto!important;
    margin-bottom:10px!important;
    padding: 4px!important;
   }

.tlistdownload{
    padding-right: 4px!important
   }

.tlistrow{
    display: block!important;
    width: 870px!important;
    border-width: 5px !important;
    border-color: #2d2d2d !important;
    border-radius: 1px!important;
    border-bottom: none !important;
    margin: 0px -1px 0px 0px!important;
   }
    
.tlistrow.hide {
    display: none!important;
}

.tlistname{
    padding: 8px 8px 8px 10px!important;
    min-width: 46.1em!important;
    max-width: 46.1em!important;
    text-overflow: ellipsis;
    white-space:nowrap;
    overflow: hidden;
   }

.tlistsize{
    min-width: 5em!important;
    text-align:center!important;
    padding: 0px 7px 0px 7px!important;
    border-width: 1px !important;
    border-color: rgba(0,0,0,0) !important;
    border-radius: 1px!important;
   }

.tlistsn, .tlistln, .tlistdn{
    min-width: 4em!important;
    min-height: 4em!important;
    padding: 0px!important;
    text-align:center!important;
    border-right: 1px solid!important;
    border-color: rgba(0,0,0,0) !important;
    border-radius: 1px!important
   }

.tlisticon{
    border-right: 1px solid!important;
    border-color: rgba(0,0,0,0) !important;
    border-radius: 1px!important;
    padding: 0px 2px 0px 0px!important;
    margin-left:-2px!important;
    float:left;
   }

.tlistfailed{
    min-width: 7em!important;
    min-height: 4em!important;
    text-align:center!important;
    padding-left: 11px!important
   }

.tlistmn{
    width: 5em!important;
    min-height: 4em!important;
    text-align:center!important;
   }

.tlistthone,
.tlistsizehead,
.tlistthtwo,
.tlistthfive,
.tlistthsix,
.tlistthseven,
.tlisttheight{
    border-top: 1px solid!important;
    border-right: 1px solid!important;
    border-color: rgba(0,0,0,0) !important;
    border-radius: 1px!important;
   }

.tlistthone,
.tlistthfive,
.tlistthsix,
.tlistthseven,
.tlisttheight{
    text-align: center!important;
    padding-left: 0px!important;
    padding-right: 0px!important;
   }

.tlistthfive,
.tlistthsix{
    width: 4em!important;
    min-height: 4em!important;
    padding: 5px 0px 5px 0px!important;
   }

.tlistsizehead{
    min-width: 4em!important;
    padding: 5px 1px 5px 1px!important;
   }

.tlistthone{
    min-width: 7em!important;
    padding: 5px 1px 5px 0px!important;
   }

.tlistthtwo{
    min-width: 43em!important;
    padding: 5px 19px 5px 19px!important;
   }

.tlistthseven{
    width: 3.65em!important;
    padding: 5px 2px 5px 2px!important;
   }

.tlisttheight{
    width: 2.9em!important;
    padding: 5px 0px 5px 1px!important;
    border-right: none!important
   }

.tlistsn{
    color: #b6cf86!important
   }

.tlistln{
    color: #d184a8!important
   }

.tlistmn{
    color: #b7c5d9!important
   }

tr.trusted.tlistrow{
    background-color: rgba(160, 224, 176, 0.1)!important;
   }

.remake{
    background: rgba(224, 144, 144, 0.1)!important;
   }

.aplus{
    background-color: rgba(144, 176, 240, 0.1)!important;
   }

.tlistpages{
    padding: 0px 0px 0px 0px!important;
    border-color: rgba(0,0,0,0) !important;
    border-bottom: none!important;
    margin-bottom: 0px!important;
   }


.page, b, .tlistsortorder{
    border: none!important;
    padding: .1em!important;
    font-size: 12px!important;
   }

.page:hover{
    background: none!important
   }

div.comment,
div.quote{
    background: #272727 !important;
    border: none !important;
    border-radius: 0px !important;
    }

td.tlisticon a img{
        display: block !important;
        margin-left: 2px;
    }

.spoiler, .quote {
        background: #272727 !important;
        border: none !important;
        border-radius: 0 !important;
    }
}
