css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.spiegel.de") {
.spCommercial, #spColumnAd, #qcTopBlocker, #spFontSizing {
display: none !important;
}

#spContainer { padding: 0px !important; width: 100% !important; min-width: 0% !important; }
#spHeader { width: auto !important; }
#spBreadcrumbLine { width: auto !important; padding: 10px 5px 8px 20px !important; }
#spMainContent { width: 650px !important }
#spColumnRight { float: right !important }
#spArticleBody { width: auto !important}
.spCenterGallery { width: 517px !important }
.spBigScreen #spMainContent,
.spSmallScreen #spMainContent  { width: 650px !important; margin: 20px 0px 10px 20px !important; }

.spBigScreen .spRessortTeaserBox { width: 650px !important; }
#spFooter { width: 600px !important; }
}
