css:
@-moz-document url("http://www.allmystery.de/"), 
url("https://www.allmystery.de/"), 
url-prefix("http://www.allmystery.de/themen"), 
url-prefix("https://www.allmystery.de/themen"),
url-prefix("http://www.allmystery.de/ena/"), 
url-prefix("https://www.allmystery.de/ena/"),
url-prefix("http://www.allmystery.de/artikel/"), 
url-prefix("https://www.allmystery.de/artikel/"),
url-prefix("http://www.allmystery.de/stats/"),
url-prefix("https://www.allmystery.de/stats/"), 
url-prefix("http://www.allmystery.de/chat/"), 
url-prefix("https://www.allmystery.de/chat/"), 
url-prefix("http://www.allmystery.de/wiki/"), 
url-prefix("https://www.allmystery.de/wiki/"),
url-prefix("http://www.allmystery.de/pn"), 
url-prefix("https://www.allmystery.de/pn"), 
url-prefix("http://www.allmystery.de/fcg"), 
url-prefix("https://www.allmystery.de/fcg"), 
url-prefix("http://www.allmystery.de/am"), 
url-prefix("https://www.allmystery.de/am"), url-prefix("https://www.allmystery.de/videos/") {
body.page, body.popup, .titelg {
    background-image:url("http://static.allmystery.de/upics/4e17bc_21.jpg") !important;
    background-size: 100% 100% !important;
    background-attachment:fixed !important;
    }

.header {
    background: none !important;
    background-color: rgba(0,0,0,0.8) !important;
    border-radius: 5px 5px !important;
    border: 1px solid rgba(0,0,0,1.0) !important;
    box-shadow: 0px 0px 20px #454545 !important;
    }

.tabnav a {
    background-color: rgba(0,0,0,0.8) !important;
    color: white !important;
    border-radius: 5px 5px !important;
    }

.tabnav a:hover  {
    background-color: rgba(255,255,255,0.5) !important;
    border-radius: 5px 5px !important;
    box-shadow: 0px 0px 40px #000000 !important;
    transition: 1s !important;
    color: #000000 !important;
    }
    
a.tabnav_selected, a.tabnav_js_selected  {
    background-color: rgba(255,255,255,0.3) !important;
    border-radius: 5px 5px !important;
    box-shadow: 0px 0px 40px #000000 !important;
    transition: 1s !important;
    color: #000000 !important;
    }  

.mcell_inhalt, .mcell, .mcell2, .mcell_left, .mcell_inhalt2, .mcell_inhalt3, div.categoryHeader, div.startHeading span, div#attention, form#post_thread, .Voting, h1, div.categories div.active, div.categories div {
    background-color: rgba(0,0,0,0.8) !important;
    border: 1px solid rgba(0,0,0,1.0) !important;
    border-radius: 5px 5px !important; 
    box-shadow: 0px 0px 20px #454545 !important;
    }

.sufu {
    border: 1px solid rgba(0,0,0,1.0) !important;
    border-radius: 5px 5px !important; 
    box-shadow: 0px 0px 20px #454545 !important;
    }

.post, .paginator {
    background-color: rgba(0,0,0,0.8) !important;
    border-bottom: 1px solid transparent !important;
    border-top: 1px solid transparent !important;
    border-radius: 5px 5px !important; 
    border: 1px solid rgba(0,0,0,1.0) !important;
    box-shadow: 0px 0px 20px #454545 !important;
    }

.post:nth-child(even), .paginatorLast, .MainOverview td.Category, .MainOverview td.LastPost, td, tr, .start .subselector, .start .areas .area, .start .areas .area:nth-of-type(odd), .start .areas .area .main, .start .areas .area .last, .videos .videoitem, .videos .videoitem:nth-of-type(even) {
    background-color: rgba(0,0,0,0.8) !important;
    border-radius: 5px 5px !important;
    border: 1px solid rgba(0,0,0,1.0) !important;
    box-shadow: 0px 0px 20px #454545 !important;
    }

.fm td, .fh td, .fd td {
    border-radius: 5px 5px !important;
    border: 1px solid rgba(0,0,0,1.0) !important;
    box-shadow: 0px 0px 20px #454545 !important;
    }

.header_info {
    border-radius: 5px 5px !important; 
    border-color: rgba(0,0,0,1.0) !important;
    border-width: 1px !important;
    border-style: solid !important;
    background-color: rgba(0,0,0,0.9) !important;
    box-shadow: 0px 0px 20px #454545 !important;
    }

.message, .message_info {
    background-color: rgba(0,0,0,0.8) !important;
    border-radius: 5px 5px !important;
    border-color: rgba(0,0,0,1.0) !important;
    border-width: 1px !important;
    border-style: solid !important;
    box-shadow: 0px 0px 20px #454545 !important;
    }

#intro {
    background-color: rgba(0,0,0,0.8) !important;
    border-radius: 5px 5px !important; 
    border: 1px solid rgba(0,0,0,1.0) !important;
    box-shadow: 0px 0px 20px #454545 !important;
    }

.Voting .ui-widget-content .ui-state-default {
    background: rgba(255,255,255,1.0) !important;
    border-radius: 2px 2px !important;
    }

.Voting-bar-filling {
    background: rgba(121,121,121,0.8) !important;
    }
  
.Voting-voted .Voting-bar-filling  {
    background: rgba(255,255,255,0.7) !important;
    }

textarea {
    border-radius: 5px 5px !important;
    box-shadow: 0px 0px 30px #454545 !important;
    }

.footer hr {
    background-color: #000000 !important;
    border-radius: 4px 4px !important;
    }

input {
    border-color: #000000 !important;
    border-radius: 5px 5px !important; 
    box-shadow: 0px 0px 20px #454545 !important;
    }

.quick_item_header {
    background-color: rgba(0,0,0,0.8) !important;
    border-bottom: 1px solid rgba(0,0,0,1.0) !important;
    border-top: 1px solid rgba(0,0,0,1.0) !important;
    border-radius: 5px 5px !important; 
    box-shadow: 0px 0px 20px #454545 !important;
    }

.quick_item_selected {
    background: rgba(0,0,0,1.0) !important;
    border-radius: 5px 5px !important; 
    box-shadow: 0px 0px 20px #454545 !important;
    }

#se {
    background-color: rgba(0,0,0,1.0) !important;
    border-radius: 5px 5px !important; 
    box-shadow: 0px 0px 20px #454545 !important;
    }

#moreMenu {
    border: 1px solid rgba(0,0,0,1.0) !important;
    background-color: rgba(0,0,0,1.0) !important;
    color: rgba(0,0,0,1.0) !important;
    border-radius: 5px 5px !important; 
    box-shadow: 0px 0px 20px #454545 !important;
    }

img.smallCategoryImage {
    border: 1px solid transparent !important;
    border-radius: 5px 5px !important;
    box-shadow: 0px 0px 20px #454545 !important;
    }

.status {
    background-color: rgba(0,0,0,0.8) !important;
    border-radius: 5px 5px !important; 
    box-shadow: 0px 0px 20px #454545 !important;
    }

.useravatar {
    border: 1px solid transparent !important;
    border-radius: 5px 5px !important; 
    box-shadow: 0px 0px 22px #ffffff !important;
    }

.in {
    background-color: rgba(0,0,0,0.9) !important;
    border-radius: 5px 5px; 
    box-shadow: 0px 0px 20px #454545 !important;
    }

a[href="javascript:pn('Verwaltung', '29904')"] {
    color: #616161 !important;
    }

a[href="javascript:pn('system', '28834')"] {
    color: #616161 !important;
    }

.fd2 {
    background-color: rgba(0,0,0,0.8) !important;
    border: 1px solid rgba(0,0,0,1.0) !important;
    border-radius: 5px 5px !important;
    box-shadow: 0px 0px 20px #454545 !important;
    }

.fh2 {
    background-color: rgba(0,0,0,0.8) !important;
    border: 1px solid rgba(0,0,0,1.0) !important;
    border-radius: 5px 5px !important;
    box-shadow: 0px 0px 20px #454545 !important;
    }

.paginator {
    border-bottom: 1px solid rgba(0,0,0,0.9) !important;
    }

.finfo {
    border-right: solid 1px transparent !important;
    }

.fbeitrag {
    border-left: solid 1px transparent !important;
    }

.status {
    background-color: transparent !important;
    }

.titelg {
    border-bottom: 1px solid rgba(0,0,0,1.0) !important;
    border-radius: 5px 5px !important;
    }

a img {
    border-color: transparent !important;
    border-radius: 5px 5px !important;
    }

.blogs-prevpost.even {
    background-color: rgba(0,0,0,0.8) !important;
    border: 1px solid rgba(0,0,0,1.0) !important;
    border-radius: 5px 5px !important;
    box-shadow: 0px 0px 20px #454545 !important;
    }

.blogs-prevpost {
    background-color: rgba(0,0,0,0.8) !important;
    border: 1px solid rgba(0,0,0,1.0) !important;
    border-radius: 5px 5px !important;
    box-shadow: 0px 0px 20px #454545 !important;
    }

.voteup {
    background-color: rgba(0,0,0,0.9) !important;
    border: 1px solid rgba(0,0,0,1.0) !important;
    border-radius: 5px 5px !important;
    box-shadow: 0px 0px 20px #454545 !important;
    }

.voteup:hover {
    transition: 1s !important;
    background-color: rgba(0,0,0,0.7) !important;
    border: 1px solid rgba(0,0,0,1.0) !important;
    border-radius: 5px 5px !important;
    box-shadow: 0px 0px 20px #ffffff !important;
    }

a.mod {
    color: #BE0808 !important;
    background-color: #000000 !important;
    border-radius: 2px 2px !important;
    border: 1px solid #000000 !important;
    }

#radio, #radio_skip, #radio_user {
    border: 2px solid #ffffff !important;
    background-color: #000000 !important;
    }

#chat_messages {
    background-color: rgba(0,0,0,0.8) !important;
    border: 1px solid rgba(0,0,0,1.0) !important;
    border-radius: 5px 5px !important;
    }

div.post.warn {
    background-color: rgba(152,0,0,0.8) !important;
    box-shadow:inset 50px 20px 80px #ff0000 !important;
    }
}

@-moz-document url("https://www.allmystery.de/fcgi/?m=messages_log"), 
 
url-prefix("https://www.allmystery.de/artikel/"), 
url-prefix("https://www.allmystery.de/stats/"), 
url-prefix("https://www.allmystery.de/wiki/"), 
url-prefix("https://www.allmystery.de/fcgi/?m=settings"), 
 
url-prefix("http://www.allmystery.de/artikel/"), 
url-prefix("http://www.allmystery.de/stats/"), 
url-prefix("http://www.allmystery.de/wiki/"), 
url-prefix("http://www.allmystery.de/fcgi/?m=settings") {
.main {
    background-color: rgba(0,0,0,0.8) !important;
    border: 1px solid rgba(0,0,0,1.0) !important;
    border-radius: 5px 5px !important;
    box-shadow: 0px 0px 20px #454545 !important;
    }
}
