css:
/* - - ニコ生ページ -　- 1.0.1*/
@-moz-document url-prefix("http://live.nicovideo.jp"){
/*top*/
.container .carousel .paddle_nav .next a{right:-22px!important;}
.container .carousel .paddle_nav .prev a{left:-18px!important;}
#live_header .score_search p .count {color: #002B55!important;}

#onair_program_area .all_list .item > a:hover,
#onair_program_area .picup_list .item_title a:hover,
#onair_program_area .picup_list .item > a:hover{text-decoration:none!important;}

.container .item_score a,.container .item_score a:visited{
 text-decoration:none!important;
}
.container .item_score a:hover{
    background:none!important;
    color: #00B7FF!important;
    text-decoration: none;
}

/*ヘッダー*/

#siteHeader #siteHeaderInner ul #siteHeaderRightMenu ul li{
 height:19px!important;
 line-height:19px!important;
}

#siteHeader #siteHeaderInner ul #siteHeaderRightMenu ul li a{padding:2px 6px 0 16px!important;}

/*視聴*/

#page #watch_zapping_box,
.container .live_topic_area .fifth_anniversary{display:none!important;}

#watch_player_top_box .livetag_box .tag_list li a.nicopedia {
 text-decoration:none!important;
 color:#000!important;
}

#watch_player_top_box .livetag_box.tag_list li a:hover{
 text-decoration: underline!important;
}

#watch_player_top_box .livetag_box .tag_list li a[title*="大百科"]{width:16px!important;}

#watch_player_top_box .livetag_box {margin-top:5px!important;}

#wall_chip_area{
 /*position:absolute!important;
 top:0px!important;
 right:26px!important;*/
 display:none!important;
}

/**/
.slider_view{width:982px!important;}

#page .slider_container{padding: 0!important;}

#page #watch_title_box{
 background-color:#2a2a2a!important;
 /*padding:10px 21px!important;*/
 width:100%!important;
 border-bottom:0!important;
}

#watch_title_box .box_inner {
    margin:-5px auto 0 auto!important;
    width: 960px!important;
}

/*#page_cover{width:999px!important;margin}*/

.title .title_text{color:#fff!important;text-shadow:0 1px 0 #2a2a2a!important;}

#watch_title_box .title a,
#watch_title_box .meta span,
#watch_title_box .meta a{
 color:#c9c9c9!important;
 text-decoration:none!important;
}

#page_cover a:hover {
    text-decoration:underline!important;
}

#watch_title_box .meta #channel_interface_bookmark{color:#ffd709!important;}

#watch_title_box .title .smooth_scroll:hover,
#watch_title_box .meta .commu_name:hover,
#watch_title_box .meta a[title]:hover,
#watch_title_box .meta .nicopedia_nushi:hover,
#watch_title_box .meta #channel_interface_bookmark:hover
{text-decoration:underline!important;}

.box_inner .meta .name a > img.dic {
    position: relative !important;
    top: 3px !important;
}

#watch_tab_box .view > .nf_detail > .information > .reserve > .icon{top:4px!important;}

/*タグ*/
#watch_player_top_box .livetag_box .tag_list {
 color: #000000 !important;padding:0!important;
}

#watch_player_top_box .livetag_box .tag_list .open{
 color: #47478d !important;display: none!important;
}

#watch_player_top_box .livetag_box .tag_list .edit {color: #2b80d5 !important;}

#watch_player_top_box .livetag_box .tag_title {padding-bottom: 10px!important;}

#watch_player_top_box .livetag_box {
 position: relative!important;
}

#watch_player_top_box .tag_title{
 margin:-1em 5px 5px 0!important;
 position:absolute!important;
 top:50%!important;
}

#watch_player_top_box .cover{
 height: auto !important;
 padding:3px 0 3px 48px!important;
}

#watch_player_top_box{
 margin:8px auto!important;
 /*min-height:38px!important;
 display: table!important;
 vertical-align: middle!important;*/
}

#watch_player_top_box .tag_list li {
 margin:1px 10px 1px 0!important;
}

/*プレイヤー下部*/
#watch_player_bottom_box .share_right #twitter-widget-0{padding-top:3px!important;}


}


/*　-- ニコ生検索ページ --　*/
@-moz-document url-prefix("http://live.nicovideo.jp/recent?tab="){
#userFrame .userBox {
    /*background: none repeat scroll 0 0 #FAFCFF !important;*/
    border: 1px solid #c9c9c9 !important;
    margin:5px 8px 5px 0 !important;
    padding-right: 1px !important;
    width: 229px !important;
}

#userFrame .userBox .audience {
    margin-left: 9px !important;
    margin-right: 15px  !important;
    padding:1px 1px 1px 18px !important;
}

#userFrame .userBox .detail {width:155px !important;}   

}


/*  -- Nsen --  */
@-moz-document url-prefix("http://live.nicovideo.jp/watch/nsen") {
#zapping_area,#zapping_area_place_holder,#zapping_controller_area {display:none!important;}

#flvplayer_container {margin-bottom:5px!important;}

#nsen_vocaloid #flvplayer_container {border: 4px solid #027873!important;}
#nsen_toho #flvplayer_container {border: 4px solid #8F1800!important;}
#nsen_nicoindies #flvplayer_container {border-bottom: 4px solid #0B508B!important;}
#nsen_sing #flvplayer_container {border-bottom: 4px solid #CF6300!important;}
#nsen_play #flvplayer_container {border-bottom: 4px solid #CE9C00!important;}
#nsen_pv #flvplayer_container {border-bottom: 4px solid #BA007B!important;}
#nsen_hotaru #flvplayer_container {border-bottom: 4px solid #0118A2!important;}
#nsen_allgenre #flvplayer_container {border-bottom: 4px solid #630079!important;}

}
