css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("filehippo.com") {

/* remove stuff */
#share-links,
#techbeat-widget,
#techbeat-widget-wide-container,
[id^="ad-slot"],
iframe {
display: none !important;
}

/* Various adjustments */

/* Tone down white BG */
#content-wrapper {
background-color: #fafafa !important;
}

/* give paragraphs black text */
p {
color: #000 !important;
}

/* some spacing around main container */
#content-main {
margin: 0 .5em !important;
}

/* Download description and Sidebar adjustments */
.tabs-container {
float: left !important;}

#program-sidebar {
position: static !important;
float: right !important;
width: 290px !important;
}

}
