css:
@-moz-document domain("wo.blox.pl") {
html body {
	color:#2a2a2a !important;
	background:#fff !important;
	font:normal 130%/140% Cambria,Georgia,serif !important;
    letter-spacing: 0.01rem;
	text-rendering: optimizelegibility;
}
body #BlogBodyBox {
	border:none !important;
	background:transparent !important;
    width: 100% !important;
    margin: 0 !important;
    padding: 0 !important;
}
body #BlogTytulyOstatnichWpisow, body #BlogAutorzyBox,
body #BlogTytulBoxaOstatnichWpisow, body #BlogAutorzyTytul {
	font-family: Cambria,Georgia,serif !important;
	font-size: 13px !important;
}
#BlogGlownyBox {
    width: 50% !important;
    min-width: 720px !important;
    padding: 10px 0 !important;
}
@media (max-width: 740px) {
    html body {
      margin: 10px !important;
      font-size: 120% !important;
      line-height: 130% !important;
    }
    #BlogGlownyBox {
      width: 100% !important;
      min-width: 310px;
      padding: 5px 0 !important;
    }
}
body div.BlogDataWpisu,
#BlogTytulBoxaOstatnichWpisow, #BlogAutorzyTytul,
#ZakladkiTytul, .TytulFolderaZakladek, #ArchiwumTytul,
#BlogKategorieBox {
	color:#666 !important;
	text-transform:none !important
}
body div[id] .BlogWpisItemTytul, body div[id] .TytulKomentowanegoWpisu{
	font-family:Cambria,Georgia,serif !important;
	letter-spacing:0  !important
}
body a:link{
	color:#9c0001 !important;
	background: transparent !important;
	text-decoration:none !important
}
body a:hover, body a:visited:hover {
	color:#c41213 !important;
	text-decoration:underline !important
}
body a:visited{
	color:#a00 !important
}

body img {
	box-shadow: 1px 1px 4px rgba(0, 0, 0, 0.3) !important;
	border: 1px solid #fff !important;
    max-width: 80% !important;
}

body #BlogTytulBox {
	background: none !important;
	height: auto !important;
    width: 100% !important;
}
body #BlogTytulText {
	display: block !important;
	font-size: 32px !important;
	font-style: italic !important;
}
body #BlogWazkaSzpalta {
	display: none !important;
}
body #BlogBodyBox > table > tbody > tr > td:last-child {
	display: none !important;
}
body p {
	margin-bottom: 15px !important;
    text-indent: 0 !important;
}
body #BlogSzerokaSzpalta {
	border-right: 0 !important;
	width: 100% !important;
    margin: 0 !important;
    padding: 0 0 5px 0 !important;
}

body #SkomentujListaKomentarzy {
	font-size: 90% !important;
	line-height: 150% !important;
}
body #SkomentujListaKomentarzy .Infopar, body #SkomentujListaKomentarzy .Infoniepar {
	margin-top: 25px !important;
    border-top: 1px solid #eee !important;
}
body #SkomentujListaKomentarzy .InfoKomentarzData {
	font-size: 80% !important;
}
body #SkomentujListaTrackback, body #PolecWpisStrKom {
	font-size: 70% !important;
}
body .SkomentujDodajKomentarzLink, body .SkomentujDodajKomentarzLinkPodLista {
	margin: 10px 0 !important;
}
body #BlogKategorieBox {
    width: 100% !important;
    font-size: 90% !important;
    line-height: 150% !important;
    border-top: 1px solid #eee !important;
    border-bottom: 1px solid #eee !important;
    padding: 0 !important;
    margin: 0 !important;
}
body #BlogKategorieBox a {
    padding: 0 5px !important; 
}
body #BlogKategorieBox td {
  display: block !important;;
}
body #BlogKategorieBox #BlogRss {
  display: none !important;
}
body div.BlogDataWpisu {
  margin-top: 30px !important;
  text-align: left !important;
  font-size: 90% !important;
}
body .SkomentujDodajKomentarzLinkPodLista {
	border-top: 1px solid #eaeaea !important;
	padding-top: 20px !important;
	margin-top: 20px !important;
}
body .SkomentujDodajKomentarzLink a, body .SkomentujDodajKomentarzLinkPodLista a {
	font-weight: bold !important;
}
body #SkomentujTytListyKomentarzy {
	font-weight: bold !important;
	font-size: 120% !important;
}
body .InfoPodTekstem {
	color: #aaa !important;
}
body .BlogWpisBox {
    margin: 10px 0 !important;
}
body .BlogWpisInfo {
    border-bottom: 1px solid #eee !important; 
}
body .BlogWpisInfo a {
    padding: 0 10px !important;
}
body .infoKomentarzTekst a {
    word-break: break-all;
}
}
