css:
/* AGENT_SHEET */

@charset "utf-8";

scrollbar thumb
{
    -moz-appearance: none !important;
    border: 0px solid rgba(255,255,255,0.6) !important;
    padding: 0 0 !important;
    background: rgb(60,194,238) !important;
    box-shadow: 0px 0px 1.5px white !important;
    opacity: 0.7 !important;
}

scrollbar[orient="vertical"] thumb
{
    background: -moz-linear-gradient(left, rgba(255, 255, 255, 0) 0%, rgba(255, 255, 255, 0) 9%, rgb(60,194,238) 10%, rgb(60,194,238) 66%, rgba(255, 255, 255, 0) 67%, rgba(255, 255, 255, 0) 100%) !important;
    min-height: 70px !important;
    width: 11px !important;
}

scrollbar[orient="horizontal"] thumb
{
    background: -moz-linear-gradient(top, rgba(255, 255, 255, 0) 0%, rgba(255, 255, 255, 0) 9%, rgb(60,194,238) 10%, rgb(60,194,238) 66%, rgba(255, 255, 255, 0) 67%, rgba(255, 255, 255, 0) 100%) !important;
    min-width: 70px !important;
    height: 11px !important;
}

scrollbar[orient="vertical"] thumb:hover, scrollbar[orient="vertical"] thumb:active
{
    opacity: 0.85 !important;
}

scrollbar[orient="horizontal"] thumb:hover
{
    opacity: 0.85 !important;
}

scrollbar
{    
    -moz-appearance: none !important;
    background: none !important;
    position: relative !important;
    border: none !important;
    z-index: 102400 !important;
}

scrollbar[orient="vertical"]
{
	width: 11px !important;
    margin-left: -10px !important;
    margin-right: -1px !important;
/*	padding:2px 1px 2px 0px!important;*/
}

scrollbar[orient="horizontal"]
{
	height: 11px !important;
    margin-top: -10px !important;
    margin-bottom: -1px !important;
/*	padding:0px 2px 1px 2px!important;*/
}

scrollbar scrollbarbutton, scrollbar scrollcorner
{
    display: none !important;
}
