css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("netvibes.com") {
  .moduleContent a:link, .moduleContent a:visited,
  .item-content a:link, .item-content a:visited {
    color: #8e8ebe !important;
  }
  .nv-quickshare-bar {
    display: none !important;
    visibility: hidden !important;
  }
  .nv-item-renderer-preview-container {
    margin: 0;
  }
  .nv-reader-item, .nv-line-item-renderer {
    padding-left: 10px !important;
    padding-right: 10px !important;
  }
  .feed.nv-item-renderer {
    margin: 0px 10px !important;
    padding: 10px 5px 10px !important;
  }
  .nv-ir-feed-head {
    max-width: 1024px !important;
  }
  .nv-ir-feed-head h2 {
    font-size: 1.7em !important;
    line-height: 1.5em !important;
  }
  .nv-ir-article-text {
    max-width: 1024px !important;
    font-size: 13px !important;
    line-height: 1.4em !important;
  }
  .nv-ir-article-text p {
    padding-top: 0.3em !important;
    padding-bottom: 0.3em !important;
    letter-spacing: 0.02em !important;
  }
  .nv-item-renderer pre,
  .nv-item-renderer code,
  .nv-item-renderer code div,
  .nv-item-renderer code span
  .nv-item-renderer.focus pre,
  .nv-item-renderer.focus code,
  .nv-item-renderer.focus code div,
  .nv-item-renderer.focus code span  {
    font-family: Consolas, Monaco, Monospace !important;
    font-size: 1em !important;
    background-color: #2e2e2e !important;
  }
}
