css:
/** minimal scrollbars for Firefox
/*  http://userstyles.org/styles/97233
/*  by freecyber
/*  update 2014-0116
*/

/* AGENT_SHEET */

scrollbar:not(#foo)
{
  position: relative !important;
  transition: all 0s !important; 
  z-index: 2 !important; 
}
html>scrollbar:not(#foo)
{
  z-index: 2147483647 !important;
}

scrollbar:not(#foo),
scrollbar:not(#foo) *
{
  -moz-appearance: none !important;
  background: none !important;
  margin: 0px !important;
  padding: 0px !important;
  border: 0px !important;
  box-shadow: none !important;
}

:not(select)>scrollbar[orient="vertical"]:not(#foo)
{
  -moz-margin-start: -12px !important;
  max-width: 12px !important;
  min-width: 12px !important;
}

scrollbar[orient="horizontal"]:not(#foo)
{
  margin-top: -12px !important;
  max-height: 12px !important;
  min-height: 12px !important;
}

:not(#foo) scrollbar:not(#foo) thumb[orient="vertical"]:not(#foo)
{
  min-height: 24px !important;
  width: 12px !important;
  min-width: 12px !important;
  max-width: 12px !important;
}

scrollbar:not(#foo) thumb[orient="horizontal"]
{
  min-width: 24px !important;
  height: 12px !important;
  min-height: 12px !important;
  max-height: 12px !important;
}

scrollbar:not(#foo) thumb
{
  border-radius: 6px !important;
  background: transparent !important;
  border: 2px solid rgba(0,255,255,0) !important;
  box-shadow: 0 0 0 8px rgba(128,128,128,.5) inset !important;
  transition: all .5s !important; 
  opacity: 1 !important; 
}

scrollbar:hover:not(#foo) thumb 
{
  box-shadow: 0 0 0 8px rgba(128,128,128,.7) inset !important;
  transition: all .5s !important; 
}

scrollbar:not(#foo) thumb:active
{
  box-shadow: 0 0 0 8px rgba(128,128,128,1) inset !important;
  transition: all .5s !important; 
}

/* no buttons */
scrollbar:not(#foo) scrollbarbutton{
  min-height: 0px !important;
  min-width: 0px !important;
  max-height: 12px !important;
  max-width: 12px !important;
  height: 0px !important;
  width: 0px !important;
}

/* autohide
:not(#foo) :not(select):not(:hover)>scrollbar:not(:hover),
:not(#foo) > scrollbar:not(#foo):not(:hover)
{ 
  opacity: 0 !important; 
  transition: all .5s !important; 
}
 */
