css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("noteflight.com") {
div#appHeaderBand
{
background-color: black !important;
}
div#content
{
background-color: whitesmoke;
}
div.headerMenuItem
{
color: white !important;
font-weight: bold !important;
}
div.headerSubMenu
{
background-color: whitesmoke !important;
}
a.headerMenuItem
{
color: grey !important;
}
a.headerMenuItem:hover
{
color: black !important;
}
.actionButton
{
background-color: whitesmoke !important;
border: none !important;
color: black !important;
}
div.headerSubMenu.infoMenu, div.info
{
background-color: whitesmoke !important;
}
a.infoNavItem:hover
{
color: white !important;
}
div.headerSearchBar
{
background-color: lightgrey !important;
}
div.headerSubMenu.standardSubMenu.searchBar
{
background-color: whitesmoke !important;
color: black;
}
div.collectionItem.clearFloat.current
{
background-color: grey !important;
}
div.collectionItem.clearFloat:hover
{
background-color: #CCCCCC !important;
}
div.infoStrip
{
background-color: grey !important;
}
div.scoreSocialPanel.scoreSocialPanel.showing, div.comment.clearFloat
{
background-color: lightgrey !important;
}
button.primary
{
background-color: whitesmoke !important;
color: black !important;
}
a.standardLink
{
color: grey !important;
}
div.scoresViewType
{
background-color: lightgrey;
}
div.scoresViewType:hover
{
background-color: #CCCCCC !important;
}
div.scoresViewType.list.selected
{
background-color: grey !important;
}
li.current
{
background-color: #CCCCCC !important;
}
li:hover
{
background-color: #ACACAC !important;
}
div.scoreRow.clearFloat.editable
{
background-color: white !important;
}
}
