css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document 
url-prefix('http://www.tumblr.com/'),
url-prefix('https://www.tumblr.com/')
{

body:not(.mceContentBody) {
    background: url('http://media.tumblr.com/tumblr_m68roeYuxo1qj5ffr.gif') #fff repeat fixed !important;
    color: #000000!important;
    font-size: 12px !important;
}

.post_full .post_permalink:before {border-right-color: #d0c3d4 !important;}

h1,h2,h3,h4,h5,h6 {
    color: #000000 !important;
}

*:not(i) {
    text-shadow: none !important;
    color: #104 !important;
}

a, a:visited {
    color: #444 !important;
}

a:hover {
    color: #000 !important;
}

#next_page_link, #previous_page_link,
div.l-content.l-content.l-content {
    border: 0px!important;
    background: rgba(255,255,255,0.6) !important;
}

.post_question, 
#left_column #posts .post .permalink {
    background: rgba(255,255,255,0.6) !important;
}

.post_question_nipple, .arrow {
    background: transparent!important;
}

#right_column .dashboard_nav_item #dashboard_controls_suggested_blogs {
    background: rgba(255,255,255,0.6) !important;
    border-top: 0px solid transparent!important;
    border-bottom: 0px solid transparent!important;
    border: 0px solid #B4EB3D;
}

#right_column .dashboard_nav_item #dashboard_controls_suggested_blogs_firt {
    background: rgba(255,255,255,0.6) !important;
    border-top: 0px solid transparent!important;
    border-bottom: 0px solid transparent!important;
}

#right_column .dashboard_nav_item #dashboard_controls_suggested_blogs .dashboard_controls_suggested_blog {
    border-top: 0px solid transparent!important;
    border-bottom: 0px solid transparent!important;
}

li.post,
div.l-content.l-content.l-content {
    background: rgba(255,255,255,0.6) !important;
    -webkit-border-radius: 20px;
    -moz-border-radius: 20px;
    border: 0px!important;
    border-radius: 20px;
    margin: auto;
    padding: 20px 20px;
}

#left_column #posts .post .post_avatar,
#left_column #posts .post .sub_avatar .sub_avatar_inner {
    border-bottom-color: transparent !important;
}

#left_column #posts .post {
    border-bottom: 0px #eee solid !important;
}

#search_controls {
    border-bottom-color: transparent !important;
    border-bottom: none!important;
    border-top: none!important;
    border: none!important;
    background: rgba(255,255,255,0.9) !important;
}

#help_docs:hover, 
#all_of_tumblr:hover,
#everyone_i_follow:hover,
#my_posts:hover {
    background: rgba(255,255,255,0.3) !important;
    border-bottom: 0px #eee solid !important;
    border-top: 0px #eee solid !important;
}

#nav .nav_item.active,
#nav .nav_item .nav_menu,
#nav .nav_item .nav_item_nipple .nav_item_nipple_pixel {
    background: rgba(255,255,255,0.6) !important;
    background-image: none !important;
    border: 0px !important;
}

#nav .nav_item .nav_menu {
    border-bottom: 0px !important;
}

#nav .nav_item .nav_menu:hover {
    color: #487A8E !important;
    background: rgba(255,255,255,0.3) !important;
}

#nav .nav_item .nav_menu a {
    color: #000000 !important;
    border-top-color: 0px #eee !important;
}

#right_column a#dashboard_switch_blog_menu_current #dashboard_switch_blog_arrow_current {
    background: none;
}

#right_column .dashboard_nav_item {
    background: none;
    background-attachment: initial;
    background-position-x: 50%;
    background-position-y: 100%;
    background-origin: initial;
    background-clip: initial;
    background-color: initial;
}

#photo_upload,
#embed_a_video {
    background: rgba(255,255,255,0.6) !important;
}

#right_column .dashboard_nav_item #hide_radar {
    background-image: none;
    background-repeat-x: no-repeat;
    background-repeat-y: repeat;
    background-attachment: initial;
    background-position-x: 0%;
    background-position-y: 0%;
    background-origin: initial;
    background-clip: initial;
    background-color: transparent;
}

#nav .nav_item .nav_menu a:hover {
    color: #487A8E!important;
}

#footer, #footer a {
    color: #000000 !important;
    background-image: none !important;
}

.no_posts_found, h1.dashboard_header, h1.dashboard_header a, h1.dashboard_header span {
    color: #ccc !important;
}

#right_column .dashboard_nav_item {
    border-bottom: 0px solid #eee !important;
    background-image: none  !important;
}

#right_column .dashboard_nav_item .dashboard_nav_title,
#right_column .dashboard_nav_item .dashboard_nav_title a,
#right_column .dashboard_nav_item a,
#right_column .dashboard_nav_item ul.dashboard_subpages,
#right_column .dashboard_nav_item ul.dashboard_subpages li a {
    color: #000000 !important;
    border-bottom: 0px solid #eee !important;
}

#right_column .dashboard_nav_item ul.dashboard_subpages li a.tracked_tag_control {
    color: #000000 !important;
    border-bottom: 0px solid #eee !important;
}

#right_column .dashboard_nav_item #dashboard_controls_radar_media_fold {
    background: rgba(255,255,255,0.6) !important;
    border-bottom: 0px solid #eee !important;
}

#show_radar a, #hide_radar a,
#hide_recommended_blogs a, #show_recommended_blogs a {
    color: #000000 !important;
}

#left_column #posts .notification .avatar {
    border-color: transparent !important;
    background-color: transparent !important;
    opacity: 0.6 !important;
}

#left_column #posts .notification .avatar:hover {
    opacity: 1 !important;
}

.dashboard_switch_blog_menu_header {
    background: rgba(255,255,255,0.6) !important;
    color: #000000 !important;
    border-bottom: 0px solid #eee !important;
}

.dashboard_switch_blog_menu_item {
    background: rgba(255,255,255,0.6) !important;
    color: #000000 !important;
    border-bottom: 0px solid #eee !important;
}

.dashboard_switch_blog_menu_footer {
    background: rgba(255,255,255,0.6) !important;
    color: #000000 !important;
    border-bottom: 0px solid #eee !important;
}

.dashboard_switch_blog_menu_wrapper {
    background: rgba(255,255,255,0.6) !important;
    border-bottom: 0px solid #eee !important;
}

#new_post_notice_container {
    color: #000000 !important;
}

#new_post_notice_container a {
    color: #000000 !important;
}

#dashboard_switch_blog_menu_current,
#dashboard_switch_blog_arrow_and_divider,
#dashboard_switch_blog_arrow_current {
    background-image: none !important;
}

#posts .post .reply_pane .nipple,
.notification_type_icon {
    display: none !important;
}

ol#posts li.post.new_post {
    background: rgba(255,255,255,0.6) !important;
}

#nav .nav_item.active {
    background: rgba(255,255,255,0.6) !important;
}

#nav .nav_item .nav_item_nipple .nav_item_nipple_pixel {
    background: rgba(255,255,255,0.6) !important;
}

.sub_avatar {
    width: 32px !important;
    height: 32px !important;
    background-color: transparent !important;
}

#right_column .dashboard_nav_item .dashboard_controls_radar_media.photo, #right_column .dashboard_nav_item .dashboard_controls_radar_media.photoset, #right_column .dashboard_nav_item .dashboard_controls_radar_media.video {
    background: rgba(255,255,255,0.2) !important;
}

ol#posts li.notification.first_notification {
    background: rgba(255,255,255,0.6) !important;
}

ol#posts li.notification.alt {
    background: rgba(255,255,255,0.6) !important;
}

.l-content_top, .l-content_bottom {
    display: none;
}

ol#posts li.notification {
    background: rgba(255,255,255,0.6) !important;
    border-bottom: 1px solid #000000;
}

#left_column #posts .notification a {
    color: #000000 !important;
    border-bottom: 0px !important;
}

ol#posts li.notification.last_notification {
    border-bottom-width: 0px!important;
}

#left_column #posts .notification blockquote {
    border-left: none !important;
}

#dashboard_switch_blog_menu {
    border: 0px!important;
    border-top: 0px!important;
    border: 0px!important;
    background: rgba(255,255,255,0.6) !important;
    -webkit-border-radius: 20px;
    -moz-border-radius: 20px;
    border-radius: 20px;
}

.dashboard_switch_blog_menu_item {
    border: 0px!important;
    border-top: 0px!important;
    border: 0px!important;
    background: rgba(255,255,255,0.6) !important;
}

.user_menu .user_menu_list a .user_menu_list_item,
#dashboard_switch_blog_menu_footer:hover {
    background: rgba(255,255,255,0.6) !important;
    border-bottom: 0px solid rgba(255, 255, 255, 0.179688);
    border-top: 0px solid black;
}

.user_menu .user_menu_list a .user_menu_list_item:hover {
    color: #487A8E!important;
}

#right_column #dashboard_switch_blog_menu_wrapper #dashboard_switch_blog_menu a#dashboard_switch_blog_menu_footer {
    border-top: 0px!important;
    border-top-width: 0px;
}

#right_column #dashboard_switch_blog_menu_wrapper #dashboard_switch_blog_menu a#dashboard_switch_blog_menu_header {
    border-bottom: 0px solid #000!important;
    border-top-width: 0px;
}

#right_column #dashboard_switch_blog_menu_wrapper #dashboard_switch_blog_menu a#dashboard_switch_blog_menu_header #dashboard_switch_blog_arrow_and_divider {
    border-top: 0px solid #F6F9FC;
}

#posts .notification.single_notification,
#posts .notification.last_notification {
    border-bottom: 0px solid #24394E;
}

#posts .notification .reply_pane, #posts .post .reply_pane,
#posts ol.notes li.reblog,
#posts ol.notes li.like {
    background: rgba(255,255,255,0.6) !important;
    border-bottom: 0px;
}

#nav .nav_item .nav_menu a,
#search_box #search_controls a {
    border-bottom: 0px solid #DEE8F1;
    border-top: 0px solid #FCFDFE;
}

#right_column .dashboard_nav_item #dashboard_controls_suggested_blogs .dashboard_controls_suggested_blog .dashboard_controls_suggested_blog_title img {
    border: 0px solid #677C8F;
}

#right_column .dashboard_nav_item .dashboard_nav_title, #right_column .dashboard_nav_item .dashboard_nav_title a, #right_column .dashboard_nav_item a, #right_column .dashboard_nav_item ul.dashboard_subpages, #right_column .dashboard_nav_item ul.dashboard_subpages li a:hover {
    color: #487A8E!important;
}

#posts .post .post_avatar {
    background-color: transparent;
    border-bottom: 0px solid #2A3B52;
}

form.dashboard_options_form .dashboard_options_form_divider {
    border-bottom: 0px solid #2A3B52;
}

element.style {
    background: rgba(255,255,255,0.6) !important;
    border-bottom: 0px solid #2A3B52;
    border-top: 0px solid #1B2D42;
}

#auto_pagination_loader {
    background: rgba(255,255,255,0.6) !important;
    border-bottom: 0px solid #2A3B52;
    border-top: 0px solid #1B2D42;
}

.follower {
    background: rgba(255,255,255,0.6) !important;
    border-bottom: 0px solid #2A3B52;
    border-top: 0px solid #1B2D42;
}

.follower.alt {
    background: rgba(255,255,255,0.6) !important;
}

.dashboard_options_form {
    background: rgba(255,255,255,0.6) !important;
    border-bottom: 0px solid #2A3B52;
    border-top: 0px solid #1B2D42;
}

.follower img.avatar {
    background: rgba(255,255,255,0.6) !important;
    border: 0px solid #C7C7C7;
}

#pagination a {
    background: rgba(255,255,255,0.6) !important;
    border-bottom: 0px solid #969CA3;
}

form.dashboard_options_form .dashboard_options_form_divider {
    background-color: transparent;
    border-bottom: 0px solid #516D85;
}

#members_pane {
    background: rgba(255,255,255,0.6) !important;
}

#posts .post blockquote {
    border-left: 0px solid gainsboro;
}

blockquote {
    border-left: 0px solid gainsboro;
}

.recommendation {
    background: rgba(255,255,255,0.6) !important;
}

#inner_footer a:hover {
    color: #000;
}

#category_list {
    background: rgba(255,255,255,0.6) !important;
    border: 0px solid #E8E8E8;
}

#inner_footer {
    background: rgba(255,255,255,0.6) !important;
}

.as_links a {
    background: rgba(255,255,255,0.6) !important;
}

.answer_container {
    background: rgba(255,255,255,0.6) !important;
    border-bottom: 0px solid #24394E;
}

#posts ol.notes li {
    background: rgba(255,255,255,0.6) !important;
}

#posts ol.notes li.answer {
    background: rgba(255,255,255,0.6) !important;
}

#right_column .dashboard_nav_item .dashboard_nav_title {
    font-weight: bold;
}

#right_column .dashboard_nav_item .dashboard_nav_title a, #right_column .dashboard_nav_item a, #right_column .dashboard_nav_item ul.dashboard_subpages, #right_column .dashboard_nav_item ul.dashboard_subpages li a:hover {
    color: #487A8E!important;
}

.logo {
    height: 43px !important;
    width: 203px !important;
}

.logo > .logo_anchor {
    background-image: url(http://media.tumblr.com/tumblr_mdr5aeg6rr1qje8rs.gif) !important;
}

.controls_section .item > a:before,
.radar_avatar:before,
#right_column:after
{display: none !important;}

#search_query{
color: #ccc !important;
}

.selection_nipple{border-bottom-color: rgba(255,255,255,.6) !important;}

.search_form_row {
background: rgba(17,0,68,.25) !important;
}

#search_field #search_form .search_form_row * {
color: #ccc !important;
}

#left_column, #right_column {
background-color: transparent !important;
}

}
