css:
@-moz-document url-prefix("http://www.oschina.net/"),url-prefix("https://www.oschina.net/") {
div.NewsToolbar,
div#OSC_Footer,
a[name^="index_banner_"] + a,
div.google_ads,
div.NewsRight > div:first-child,
div.syntaxhighlighter > div.toolbar,
div.toolbar,
div[id^="OSChina_News_"] > p:first-child,
div.Content > p > a,
a[title="极光推送"],
#DevCloud,
.adsbygoogle,
div#OtherQuestionsOfUser + div,
#Share,
#p_fullcontent > p:nth-child(1) > a,
div.p_ad,
[id^="bdimgshare_"],
.recruitment.recruitment-blog,
  .recruitment,
.poster-ali,
.share,
.NewsToolbar
{
  display:none!important;
}

div#upprev_box
{
  top: 0px !important;
  bottom: unset !important;
}
}

@-moz-document url-prefix("http://my.oschina.net/") {
div.BlogEntity + div,
div#OSC_Footer,
div.BlogShare,
div.BlogLinks > p,
.adsbygoogle,
.BlogTitle + p,
.syntaxhighlighter .toolbar,
div.BlogCopyright,
  .recruitment,
  .BlogTitle + a
{
  display:none!important;
}

div#RelativeBlogs
{
  top: 0px !important;
  bottom: unset !important;
}
}
