css:
@-moz-document domain("mail.google.com") {
.aki.pp {
    margin-left:0 !important;
    padding:6px 0 11px !important;
    min-height:29px !important;
    position:relative !important;
    z-index:4 !important;
}
.aki.pp * {
    /*color:#EEE !important;*/
    box-shadow:none !important;
    padding-left:0 !important;
}
.aki.pp .akh { /*current location (Gmail/Contacts)*/
    display:none !important;
}
.aki.pp .akh:last-child { /*current location (Gmail/Contacts)*/
    display:block !important;
    padding-left:15px !important;
}
.aki.pp .J-M { /*drop-down list (Gmail/Contacts/Tasks)*/
    display:block !important;
    position:static !important;
    background:none !important;
    opacity:1 !important;
    padding:0 !important;
    margin-left:17px !important;
    border:0 !important;
}
.aki.pp .SK {
    margin-left:-0.25em !important;
    overflow:auto !important;
    border:0 !important;
}
.aki.pp .J-N { /*menu item*/
    float:left !important;
    padding:2px 0 !important;
    /*border-radius:8px !important;*/
}
.aki.pp .J-N.ake { /*currently selected*/
    /*background-color:rgba(35,35,35,0.4) !important;*/
}
.aki.pp .J-N.J-N-JT { /*hovered over*/
    /*background-color:rgba(35,35,35,0.85) !important;*/
    border:none !important;
}
.aki.pp .J-N div { /*item text*/
    margin:0 0.25em !important;
}
.aki.pp .J-N.ake div { /*current item text*/
    /*border-bottom:2px solid #EEE !important;*/
}
}
