css:
@-moz-document domain("stackexchange.com"), domain("mathoverflow.net") {
p {color:white;}  
a.post-tag {background-color:#500000}  
/**div#header {background-color:black;}   **/             
div.question-summary.narrow.tagged-interesting {background-color:#300000 ;}      
blockquote {background-color: #B8B8B8    ;}
div.container {background: black url('http://i.stack.imgur.com/PScTp.png') repeat-x top center !important;} 
a#nav-questions {color:white;}                                       
a#nav-tags {color:white;}          
a#nav-users {color:white;}
a#nav-badges {color:white;}
a#nav-unanswered {color:white;} 
a#nav-askquestion {color:white;}  
#hlogo a {
  background: transparent url('http://i.stack.imgur.com/Nl7DV.png') no-repeat left top;      
}     
div.question-status h2 {font-family:arial; font-style:italic; color: black;}  
div.sub-header-links.fr a {color:white;}        
div.user-about-me.note {background-color:black;}  
ul li {color:white; font-family:Arial;}      
h1, h2, h3, h4, h5, h6 {color:white;}     
html { min-width: 1000px } 
ul.action-list li {color:black; font-weight:bold;}     
span.popup-title {font-weight:bold; color:black;}         
span.post-tag {background-color:#500000}    
div.dashboard-num {/**font-weight:bold;**/ color:#E8E8E8}          
a {color:red;} 
div.dashboard-title a {color:red;}     
div.dashboard-description {color: #FFCCFF}    
a:visited {color: #A00000  }  
a.post-tag.moderator-tag {background-color:#686868;}       
span.post-tag.moderator-tag {background-color:#686868;}        
div.nav.mainnavs ul li a:hover {background-color: rgba(220,220,220, 75);}               
div.tag-suggestions div p {color:black;}                                                                
span.comment-copy {color:FireBrick;}    
div.user-panel div.subheader a{color:white;}                                                                                               
div#footer {background:white url('http://i.stack.imgur.com/PScTp.png') repeat top center;}                         
div.top-footer-links a {color:white;}                
div#footer.categories div.footer-wrap div#footer-menu div#footer-sites table tbody tr td ol li a {color:white;}                           
div#copyright {color:white;}            
div.topbar {background-color:black;}   
div.topbar div.topbar-wrapper div.topbar-links div.links-container span.topbar-menu-links {color:white;}          
span.hidden-text {display:block !important;}
/**i.youarehere {background-color: rgba(220,220,220, 75);} **/   
div.question-status ul li {font-family:arial; color:black;}    
ul.wmd-button-row {background-color:rgb(250,250,255);}     
ul.wmd-button-row li:hover {background-color:white;}   
div.question-status p {color:black;}     
div.modal-content ul li {color:black;} 
form div h2 {color:black;}     
ul li a#nav-askquestion:hover {background-color:rgba(220,220,220, 75);}      
ul li, ol li {color:white;}    
blockquote p {color:black;}  
/**y**/    
}
