@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);



/* LOADING - Animated Loading Throbber: */

tab[busy] .tab-icon-image
{
  list-style-image: url("data:image/gif;base64,") !important;
}





/* STATIC - Regular Throbber when not loading a site: */

tab .tab-icon-image
{
  list-style-image: url("data:image/x-icon;base64,") !important;
}
