css:
@-moz-document domain(securityfocus.com) {
        /* Get rid of unnecesary cruft. */
        #header_bar,
        #nav,
        td[width="160"],
        td[width="120"],
        td[width="150"],
        .headerButtonBar,
        div[id *= "comments"],
        a[href="#comments"],
        #articleTools{
              display: none !important;
        }

        /* Set the text off a bit from the left side of the window. */
        #article{
              margin-left: 1em !important;
        }

        /* Remove horizontal scrollbars. */
        table, #logo_new{ width: 100% !important; }
}
