css:
@-moz-document domain("musicmeter.nl") {
/* Voor suggesties, PM Urbanoetang */

/* Lichtgrijze achtergrond, zwarte tekst */
body,
#sitesearch { background:#f5f8fa !important; color:#17191a !important; }

/* Blauwe achtergrond, zwarte tekst */
#header, #footer_to_top,  .album_action_updates, .user_action_updates, .topic_action_updates, .artist_action_updates, div.button, a.button, span.button, input[type="submit"], input[type="button"] { background:#16a1d9 !important; color:#17191a !important; }

/* Zwarte achtergrond, witte tekst */
#footer, #bar { background:#17191a !important; color:#f5f8fa !important; }

/* Zwarte achtergrond, blauwe tekst */
#subbar .subbar_inner a.active, #subbar .subbar_inner a:hover, .sections a.active, .sections a:hover, #myItemsContentButtons #myItemsModes span, .paginator .page_link.active, .options.pages a.active, .options.pages a.active { background:#17191a !important; color:#16a1d9 !important; }

/* Meer blauwigheid */
.subbar_inner .divider, #bar #bar_menu a:hover { color:#16a1d9 !important; }


/* Berichten volledige breedte */
.message, hr.message_divider { width: 100% !important; }
.message_contents { padding-right:20px; }

/* Berichten met afwisselende achtergrond */
.message { margin:0 !important; padding:20px 0 20px 2px !important; background:#e6e9eb !important; }  
.message:nth-of-type(2n), #message_new { background:transparent !important; }
.message:last-child { margin-bottom:20px !important; }  
.paginator { margin-bottom:20px !important; }
#message_new { padding:0 !important; margin:20px 5px !important; }
hr.message_divider { background: #b2b2b2 !important;
border-top: 1px solid #b2b2b2 !important;
margin: 0 !important; }

/* Kader om quotes */
.message_contents .quote1, .message_contents .quote2 { background:#dcdfe0 !important; border:1px solid #17191a !important; margin:10px !important } 

/* Direct stemmen (zoals in het oude design */

#album_myvote_popup { display:block !important; background:transparent !important; position:relative !important; padding:0; margin:0; -webkit-box-shadow:none; box-shadow:none; -moz-box-shadow:none; border:none !important; top:10px !important; left:10px !important; width:220px !important;  }
#album_details_links h2 { margin-top:40px !important; } 

.album_myvote_label, .album_average.no_votes, .album_myvote.no_votes, #album_myvote_popup:after, .album_myvote { display:none !important; }
}
