css:
@-moz-document domain("www.baidu.com") {
/*顶栏文字效果*/
#u a:hover{color:orange!important;text-shadow:2px 2px 3px grey;}

/*去链接下划线*/
*{text-decoration:none !important;}

/*文字*/
*{font-family: 微软雅黑,arial,sans-serif !important;}

/*搜索栏*/
span.btn_wr{background:transparent!important;border:none!important;}
#su1,input.s_btn{
	font-weight:600;
	opacity:0.6;
	border:1px solid rgba(65,65,65,0.6)!important;
    border-right:2px!important;
    border-bottom:2px!important;
    border-style:solid!important;
    border-color:rgba(55,55,55,.6)!important;
    box-shadow:2px 2px 3px #555!important;
    color:rgb(30,30,30)!important;
    }
#su1:hover,input.s_btn:hover{color:rgba(0,0,0,.8)!important;text-shadow:2px 2px 3px grey;letter-spacing: 1px;opacity:0.7;}
.s_btn_wr,#kw.s_ipt{background:transparent!important;}
.s_ipt_wr{background:rgba(200,200,200,.4)!important;}
.s_ipt_wr:hover{background:rgba(210,210,210,.4)!important;}
.s_ipt{background:transparent!important;}
}

@-moz-document url-prefix("http://www.baidu.com/s?"), url-prefix("http://www.baidu.com/baidu?") {
/*搜索结果页面背景*/
body{
	background:url(http://imgsrc.baidu.com/forum/pic/item/d51d054f78f0f736730be1940a55b319eac41323.jpg) no-repeat center center fixed !important;
	background-size:100% auto !important;
	}

/*搜索页右栏去除*/
#content_right{display:none!important;}

/*搜索结果页面搜索栏*/
.s_ipt_wr{background:rgba(240,240,240,.3)!important;}
.bdsug{background:transparent !important;box-shadow:0px 0px 7px #888!important; }
.bdsug li{background:rgba(255,255,255,.8) !important;}
.bdsug li:hover{background:rgba(210,210,210,.9) !important;}

/*字体*/
a:link{color:#0013ca!important;}
a:visited{color:#010d7d!important;}
#content_left a:hover,#rs a:hover,#content_right a:hover{text-shadow:2px 2px 3px grey; } 
em{color:#e53600!important;}
#s_tab>a,#u *{color: rgb(51, 51, 51)!important;}
#s_tab>a:hover,span.s_help a:hover{color:rgba(100,100,100,.4)!important;text-shadow:2px 2px 3px grey;}
.op-zx-tv-video-update{color:rgb(133,115,36)!important;}
.op_pm25_container1,.op_pm25_container2,.op_pm25_container3,.op_pm25_container4,.op_pm25_container5,.op_pm25_container6 {color: rgb(51, 51, 51)!important;}

/*搜索脚印页码*/
#page a, #page strong {background:transparent!important;}
span.pc,p#page>a.n{background:rgba(255,255,255,.6)!important;font-weight:600!important;border-radius:5px;}
a>span.pc:hover,p#page>a.n:hover{color:#fff!important;background:rgba(2,2,2,.4)!important;font-weight:600!important;border-radius: 5px;}
strong>span.pc{background:rgba(255,255,255,.8)!important;}
span.fk,p.to_tieba{display:none;}
span.fk.fk_cur,span.fk:hover{opacity:1;}

/*去广告*/
span.tools,div.fsblock,#ec_im_container,.EC_mr15,td>div>embe,.opr-result,#ec-siva-wrapper,.content_right_top_hint,.ad-material-wrapper{display:none!important;} /*new*/
  
/*顶栏google样式*/
#s_tab{background:rgba(255,255,255,.3)!important;position:absolute !important;padding:4px 0px 5px 10px!important;top:0px;left:8px;width:99.3% !important;}
#u{top:4px!important;}
.s_logo{position:relative !important;top:40px;left:40px;}
form.fm{position:relative !important;top:2px;left:160px;width:650px!important;}
#head{background:rgba(255,255,255,.6)!important;width:100%!important;height:100px!important;padding-left:0!important;position:relative !important;top:-6px;left:0px;}
.s_nav{position:relative !important;top:0px;left:-10px;}

/*结果项透明*/
#content_left>*>*,#content_left div{background:transparent!important;}
.op_zhixincartoon_left>*,.op_zhixincartoon_tabs>*,.op_zhixincartoon_ai>*,.op_zhixincartoon_ai>*>*,.op_zhixincartoon_video_menu>*,ul{background:transparent!important;}  
.c-tabs-nav-li,.op_zx_baike_main,.op_zx_tieba,.op_zx_tv_video_main>*>*,.op_zx_tv_video_tabs,.op_zx_tv_video_menu,.op_zx_tv_video_tab,.op_zx_tv_video_cont,.op_zx_tv_video_tab_curcont *,.op_zx_tv_video_conts,.op_tv05_tvtabs *,.op_tv05_tvtabs,.op-zx-tv-video-finish, .op-zx-tv-video-update{background:transparent!important;} 
#ala_img_hoverview{display:none!important;}
#content_left{background:rgba(255,255,255,.6)!important;padding:20px 0px 0px 25px;position:relative!important;left:20px;}
.likenum{opacity:.7}
#wrapper{min-width:1000px!important;width:100%!important;}
.container_l #content_right {width: auto!important;}
p.op-softdown-btnbox>a{background:rgba(225,225,225,.6)!important;border-radius:9px;left:0px!important;}
p.op-softdown-btnbox>a:hover{background:rgba(245,245,245,.6)!important;text-shadow:0px 0px 5px grey;}
tbody>tr,tbody>tr>th,.c-tabs-nav>li{background:transparent !important;}

/*顶栏+底栏*/
div#u{padding:1px 6px 1px 1px;background:rgba(255,255,255,.0) !important;border-radius:10px 10px 10px 10px!important;}
#u .c-icon{display:none!important;}
.userMenu{right:-3px!important;}
div#foot{background:rgba(255,255,255,.6)!important;height:20px!important;text-align: right!important;padding-right:20px;}
a.s_logo>.bg{opacity:0!important;}
.s_logo{background:url("http://g.hiphotos.bdimg.com/album/w%3D1600%3Bq%3D90/sign=a952f969b58f8c54e3d3c1290a191684/dbb44aed2e738bd4b51f8db6a08b87d6277ff97e.jpg") no-repeat center center  !important; 
        background-size: 100% 90%  !important;}
#search{background:rgba(255,255,255,.6)!important;width:100%!important;padding-left:0px!important;}
#search>form{padding-left:35px!important;}
#rs{background:rgba(255,255,255,.5)!important;width:100%!important;margin: 0px!important;margin-top:30px!important;}
#rs>*,#rs>*>*>*>td{background:transparent !important;}
#rs_top{display:none;}	

/*底端搜索栏悬浮*/
#search{position:fixed !important;
        top:99%!important;
        z-index:99 !important;
        opacity:.5 !important;
        margin:0 !important;
        bottom:0 !important;
        background:rgba(250,250,250,.8)!important;
        height:6px!important;}
#search>form{width:920px!important;margin:auto!important;}
#search:hover{top: auto !important;
        left:auto!important;
        right:auto!important;
        opacity:1 !important;
        transition: height .2s;
        height:45px!important;}

/*搜索页音乐透明修复*/
#tangram-progressBar--TANGRAM__a-bar.tangram-progressbar-bar {background:rgba(25,25,25,.2)!important;}
#tangram-slider--TANGRAM__4-thumb.tangram-slider-thumb{background:rgba(112,112,112,.6)!important;border-radius:10px!important;}
.c-icon{opacity:.7!important;}
.c-btn{background:transparent !important;}

/*首页新闻搜索*/
#main>#search,#gotoPage{display:none;}
.content{background:rgba(255,255,255,.7)!important;padding:18px 0 10px 15px!important;border-left: 3px solid rgba(250, 250, 250,.8)!important;}
.content_bg{border:none!important;}
#side{background:rgba(255,255,255,.7)!important;padding:15px 0 5px 20px!important;}
a.s_logo>img{opacity:0;}
}
