css:
/* This style is part of the Maroon Project to make Firefox more like Opera */
/* Homepage: http://www.maroonproject.wordpress.com */

@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);
@-moz-document url(chrome://browser/content/browser.xul){

/* Padding for searchbar-searchengine */
.searchbar-engine-button {
padding-left: 2px !important;
}
/* Padding for urlbar icons */
#urlbar-icons {
padding-right: 5px !important;
padding-left: 3px !important;
}
/* Hide buttons in searchbar and urlbar */
.search-go-button,
.autocomplete-history-dropmarker,
#urlbar-go-button,
#go-button {
display: none !important;
}
/* round corners or searchbar and urlbar */
.searchbar-textbox,
#urlbar {
border-radius: 4px !important;
}
/* round corners for toolbar button hover */
.toolbarbutton-1:hover {
border-radius: 4px !important;
box-shadow: none !important;
}
/* Style the Firefox button */
#appmenu-button {
border: 1px solid #767676 !important;
border-top:none!important;
border-bottom-left-radius: 4px !important;
border-bottom-right-radius: 4px !important;
background: #eeeeee !important;
}
/* Sets the space around the bookmark toolbar icons */
#personal-bookmarks toolbarbutton {
margin: 3px 0px 3px 2px !important;
padding: 3px 6px 2px 4px !important;
border-radius: 4px !important;
}
/* Sets the space around the bookmark toolbar text */
#personal-bookmarks .toolbarbutton-text {
margin: 0px 0px 0px 0px !important;
padding: 0px 0px 0px 0px !important;
}
/* Make menubar more legible (show by pressing Alt) */
#menubar,
#main-menubar {
background: none !important;
}
#file-menu,
#edit-menu,
#view-menu,
#history-menu,
#bookmarksMenu,
#tools-menu,
#helpMenu {
color: black !important;
background: none !important;
}
/* Findbar style and move to top */
findbar {
-moz-box-ordinal-group:0 !important;
margin-top:-1px !important;
border-bottom: 1px solid #aaaaaa !important;
padding-top: 3px !important;
padding-bottom: 3px !important;
}
.findbar-textbox {
border-radius: 4px !important;
border: 1px solid #aaaaaa !important;
}
}
