css:
/* This style is part of the Maroon Project to make Firefox more like Opera */
/* Homepage: http://www.maroonproject.wordpress.com */
/* Version 0.3 - Style for Speed Dial */

@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

image[src*="chrome://speeddial/skin/icon16.png"]
{
background: url("data:image/png;base64,") !important;
padding-left: 16px !important;
}

@-moz-document url("chrome://speeddial/content/speeddial.xul"){

.sd-tab:not([selected="true"]) {
background: #f2f2f2 !important;
color: #444444 !important;
border-right: 1px solid #b4b4b4 !important;
border-bottom: 1px solid #b4b4b4 !important;
border-left: 1px solid #b4b4b4 !important;
border-top: 1px solid #b4b4b4 !important;
border-radius: 17px !important;
font-weight: normal !important;
height: 35px !important;
padding-left: 13px !important;
padding-right: 13px !important;
padding-top: 3px !important;
margin-left: 12px !important;
margin-right: 12px !important;
margin-top: 0px !important;
margin-bottom: 0px !important;
padding-bottom: 3px !important;
text-align: center !important;
}
    
.sd-tab:hover:not([selected="true"]) {
background: #f8f8f8 !important;      
}
    
.sd-tab[selected="true"] {
background: #ffffff !important;
color: #333333 !important;
border-right: 1px solid #b4b4b4 !important;
border-bottom: 1px solid #b4b4b4 !important;
border-left: 1px solid #b4b4b4 !important;
border-top: 1px solid #b4b4b4 !important;
border-radius: 17px !important;
font-weight: normal !important;
height: 35px !important;
padding-left: 13px !important;
padding-right: 13px !important;
padding-top: 3px !important;
margin-left: 12px !important;
margin-right: 12px !important;
margin-top: 0px !important;
margin-bottom: 0px !important;
padding-bottom: 3px !important;
text-align: center !important;
}
    
.sd-tabs {
background: #f6f6f6 !important;
padding: 20px !important;
}
.sd-tabs-left,
.sd-tabs-right {
border: 0px !important;
}
.sd-tabpanel {
background: #f6f6f6 !important;

}
.thumbnail{
opacity: 1 !important;
margin: 2px !important;
}
.speeddial-container {
border-width: 1px !important;
border-radius: 9px !important;
border-color: #999999 !important;
}

.thumbnail-title,
.speeddial-top-middle,
.speeddial-bottom-middle,
.speeddial-top-middle,
.speeddial-middle-left,
.speeddial-middle-right,
.speeddial-bottom-middle,
.speeddial-top-left,
.speeddial-top-right,
.speeddial-bottom-left,
.speeddial-bottom-right{
display: none;
}
}
